import React from 'react';

import { Link as RouterLink } from "react-router-dom";

import srcMenu from "../../images/icons/modified/menu.svg";

import {
    withRouter
} from "react-router-dom";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
    DirectLink
} from 'react-scroll';

import * as ReactGA from "react-ga";


const NavBarDuringEvent = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    return (
        <nav
            role="navigation"
            className="navbar navbar-expand-lg">
            <a
                href="/"
                className="navbar-brand font-weight-500"
                onClick={evt => {
                    ReactGA.event({
                        category: 'Landing BCF',
                        action: 'Click Big Career Fest. Online',
                        label: 'NavBar'
                    });
                }}>
                Big Career Fest. Online
            </a>

            <button
                className="navbar-toggler"
                type="button"
                data-toggle="collapse"
                data-target="#navbarText"
                aria-controls="navbarText"
                aria-expanded="false"
                aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon">
                        <img
                            src={srcMenu}
                            className="menu-toggler"/>
                    </span>
            </button>

            <div
                className="collapse navbar-collapse"
                id="navbarText">
                <ul className="navbar-nav ml-auto mr-auto"/>
                <ul className="navbar-nav">
                    <Link
                        onClick={evt => {
                            ReactGA.event({
                                category: 'Landing BCF',
                                action: 'Click Get Job',
                                label: 'NavBar'
                            });
                        }}
                        activeClass="active"
                        className="test2"
                        to="registration"
                        spy={true}
                        smooth={true}
                        duration={1000}
                    >
                        <li
                            className={
                                isTabletOrMobileDevice
                                    ? "nav-item nav-item-text font-weight-400"
                                    : "nav-item nav-item-text font-weight-500"
                            }>
                            Отримати роботу
                        </li>
                    </Link>
                </ul>

                <ul className="navbar-nav ml-auto">
                    {/*<li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="activities"
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click Activities',
                                        label: 'NavBar'
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Активності
                            </a>
                        </Link>
                    </li>

                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to={"companies"}
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF / NavBar',
                                        action: 'Click Companies',
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Компанії
                            </a>
                        </Link>
                    </li>*/}

                    <li className="nav-item">
                        <a
                            target={"_blank"}
                            href={"https://youtu.be/W7ipkmhsVPM"}
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF / NavBar',
                                    action: 'Click YouTube Hour With Company',
                                });
                            }}
                            className={
                                isTabletOrMobileDevice
                                    ? "nav-link font-weight-400"
                                    : "nav-link font-weight-500"
                            }>
                            Година з компанією
                        </a>
                    </li>

                    <li className="nav-item">
                        <a
                            target={"_blank"}
                            href={"https://youtu.be/c31lbf-l9l8"}
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF / NavBar',
                                    action: 'Click YouTube Lectures',
                                });
                            }}
                            className={
                                isTabletOrMobileDevice
                                    ? "nav-link font-weight-400"
                                    : "nav-link font-weight-500"
                            }>
                            <span className="bunch-bcfo-underline">
                                Дивитись лекцію
                            </span>
                        </a>
                    </li>
                </ul>
            </div>
        </nav>
    );
};

export default withRouter(NavBarDuringEvent);
