import React from 'react';

import { Animated } from "react-animated-css";

import Countdown from 'on-react-countdown';

import CardTest from "./CardTest";

const BlockTests = () => {
    return(
        <div>
            <div className="container padding-test-top">
                <div className="row">
                    <div className="col-12 col-lg-6">
                        <p className="stand-banner-title">Тестування</p>
                        <p className="stand-banner-explanation">
                            Перевір свої скіли за допомогою завдань від різних компаній. За їх успішне виконання ти можеш отримати класні призи: онлайн-диплом, сертифікат, або ж навіть запрошення на пробну співбесіду у компанію!
                        </p>
                    </div>
                    <div
                        className="col-12 col-lg-6 align-center justify-content-center-desktop">
                        <div>
                            {/*<div className="col-12 text-center">*/}
                                {/*<p className="countdown-banner-text">*/}
                                    {/*Залишилось до ярмарки*/}
                                {/*</p>*/}

                                {/*<div className="countdown-timer mt-4">*/}
                                    {/*<Countdown*/}
                                        {/*alwaysDoubleDigit={true}*/}
                                        {/*wordsOff={true}*/}
                                        {/*text={{*/}
                                            {/*days: ['день', 'днів', 'днів'],*/}
                                            {/*hours: ['година', 'годин', 'годин'],*/}
                                            {/*minutes: ['хвилина', 'хвилини', 'хвилин'],*/}
                                            {/*seconds: ['секунда', 'секунд', 'секунд'],*/}
                                        {/*}}*/}
                                        {/*end={1601107200}*/}
                                    {/*/>*/}
                                {/*</div>*/}
                                {/*<div className="names-count-row justify-content-center">*/}
                                    {/*<p className="names-count">Днів</p>*/}
                                    {/*<p className="names-count hours-margin">Годин</p>*/}
                                    {/*<p className="names-count minutes-margin">Хвилин</p>*/}
                                    {/*<p className="names-count">Секунд</p>*/}
                                {/*</div>*/}
                            {/*</div>*/}
                            <p className="countdown-banner-text">Ярмарок почався!</p>
                            <p className="banner-time">26-27 вересня. 11:00 - 18:00</p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container-fluid padding-tb-60">
                <div className="row">
                    <div className="col-12">
                        <p className="gift-company-header font-weight-600">
                            Тестування
                        </p>
                    </div>
                </div>

                <div className="row margin-top-10">
                    <div className="col-12 text-center">
                            <span className="gift-company-subheader">
                                Проходь тестування та потрапляй на співбесіду у компанію!
                            </span>
                        <br/>
                        <span className="gift-company-subheader margin-top-20">
                            Деталі читай у карточках нижче
                        </span>
                    </div>
                </div>

                <div className="row margin-top-20">
                    <CardTest
                        image={"gift-metinvest"}
                        name={"Metinvest"}
                        description={"Проходь тестування, отримуй фідбек та потрапляй на стажування для юристів Metinvest."}
                        longDescription={"Професійні тести для юристів. Приз: отримуй фідбек за твоїми результатами тестування. Також, цей тест значно підвищує твої шанси потрапити на стажування для юристів від компанії. Перейди за посиланням нижче та перевір свої знання на міцність."}
                        links={[
                            "Тестування для юристів: https://bigcareerfest.online/metinvest/#metinvest-vacancies",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                    <CardTest
                        image={"test-goldman"}
                        name={"Goldman Sachs"}
                        description={"Пройди ІТ-тест та збільшуй шанси потрапити до команди Goldman Sachs."}
                        longDescription={"Саме за ним компанія відбирає програмістів до своєї команди. P.S. Фінансовий бекграунд неважливий!"}
                        links={[
                            "Тестування HackerRank Resources: https://www.hackerrank.com/test/sample",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                    <CardTest
                        image={"test-intertop"}
                        name={"Intertop"}
                        description={"Команда Інтертоп підготувала для тебе ТРИ крутих тестування з різних галузей."}
                        longDescription={`Команда Інтертоп підготувала для тебе ТРИ крутих тестування з різних галузей. Проходь їх, перших три переможця отримають лімітовані футболки INTERTOPxНОК.`}
                        links={[
                            "Тестування з інтерет-маркетингу: https://docs.google.com/forms/d/e/1FAIpQLScqaerZqKe2W0oJ3VTxUdQ1mE5y6Yk5PzQHeDxtLFl-7kmICw/viewform",
                            "Тестування з ІТ: https://docs.google.com/forms/d/e/1FAIpQLSfPfbdGHasc1EEszx6LoDdYZDgFjuE1uVPxtoxiclpKQ-V-wA/viewform.",
                            "Тестування з e-commerce: https://docs.google.com/forms/d/e/1FAIpQLSfjrKb_48lZwk4RFCwnAvx1ZDgIJH32ayu4uU5UdtdkBNXX6Q/viewform"
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                </div>

                <div className="row margin-top-20">
                    <CardTest
                        image={"test-roland"}
                        name={"Roland Berger"}
                        description={"Подавайся на вакансію Research Specialist та отримуй тест."}
                        longDescription={
                            <div>
                                <b>Research Specialist</b>

                                <br/><br/>

                                Apply Now

                                <br/><br/>

                                Job Description

                                <br/><br/>

                                We are currently looking for a young ambitious specialist to join our newly formed Research Department based in Kyiv. Research Specialists provide research support for all Roland Berger offices across Europe, assist consulting project teams worldwide with in-depth analysis and market assessment, conduct information search and analysis, as well as carry out short-term project tasks and conduct internal studies.

                                <br/><br/>

                                Qualifications

                                <br/><br/>

                                We search for students in the 5th year or recent graduates with excellent GPA and some work experience or 1—2 internships. Our candidates have fluent spoken and written Ukrainian, English and German or French; outstanding analytical and methodological skills; as well as advanced command of MS Office. They are able to work independently and are self-motivated. Experience with commercial databases is an advantage but not required.

                                <br/><br/>

                                Additional Information

                                <br/><br/>

                                Are you a game changer and want to shape the future with us? Apply now online.

                                <br/><br/>

                                APPLY NOW

                                <br/><br/>

                                Interested? We would be happy to answer all your questions. Please contact Tatiana Sheremet at Tatiana.Sheremet@rolandberger.com. You could also simply submit your CV and a short motivation letter in English stating clearly why you are suitable for this job.
                            </div>
                        }
                        links={[
                            "Надсилай своє резюме на пошту та отримуй тест: Tatiana.Sheremet@rolandberger.com",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                    <CardTest
                        image={"test-roland"}
                        name={"Roland Berger"}
                        description={"Подавайся на вакансію Research Specialist (Financial Services) та отримуй тест."}
                        longDescription={
                            <div>
                                <b>Research Specialist (Financial Services)</b>

                                <br/><br/>

                                Apply Now

                                <br/><br/>

                                Job Description

                                <br/><br/>

                                Financial Services is your passion? You're interested in the international Banking and Insurance sector and you have excellent analytical capabilities?

                                <br/><br/>

                                We are currently looking for a young ambitious specialist to join our Research Department based in Kyiv supporting our consultants in the Global Financial Services practice. As Financial Service Research Specialists you support all Roland Berger offices across Europe with focus on international banking and insurance industry. You assist consulting project teams worldwide with in-depth analysis and market assessments by conducting information search and analysis, as well as carrying out short-term project tasks and supporting internal studies.

                                <br/><br/>

                                Qualifications

                                <br/><br/>

                                We search for students in the 5th year or postgraduate in economics with excellent GPA and first working experience in Financial Service related industries or 1—2 relevant internships. You provide outstanding analytical and methodological skills and you have advanced command of MS Office. Experience with commercial databases is an advantage but not required.

                                <br/><br/>

                                You are able to work independently, you are self-motivated, service-oriented and you have a high affinity to Financial Service. You have excellent oral and written communication skills in Ukrainian, English and French.

                                <br/><br/>

                                Additional Information

                                <br/><br/>

                                Are you a game changer and want to shape the future with us? Apply now online.

                                <br/><br/>

                                APPLY NOW

                                <br/><br/>

                                Interested? We would be happy to answer all your questions. Please contact Tatiana Sheremet via Tatiana.Sheremet@rolandberger.com. You could also simply submit your CV and a short motivation letter in English stating clearly why you are suitable for this job.
                            </div>
                        }
                        links={[
                            "Надсилай своє резюме на пошту та отримуй тест: Tatiana.Sheremet@rolandberger.com",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                    <CardTest
                        image={"test-roland"}
                        name={"Roland Berger"}
                        description={"Подавайся на вакансію Intern Research та отримуй тест."}
                        longDescription={
                            <div>
                                <b>Intern Research</b>

                                <br/><br/>

                                Apply Now

                                <br/><br/>

                                Job Description

                                <br/><br/>

                                To support our international research team, we are looking for a Research Intern for our location in Kyiv. You are ready to work at least six months providing short-term information on current projects, examining development trends and assisting in the creation of complex cross-industry enterprise and market analyzes. To do so, searching in databases and other relevant media. In addition, you support us in maintaining internal know-how management.

                                <br/><br/>

                                Qualifications

                                <br/><br/>

                                You have completed or have just completed the fourth semester of an economics-oriented degree program with above-average success. Your extensive analytical skills, your creativity in the structuring and solution of problems as well as your awareness of quality have already been proven during initial internships. You also have a high level of service and flexibility. Good knowledge of the MS-Office standard applications as well as very good communication skills in Ukrainian, English and good knowlaged in Polish/Italian/Spanish complete your profile.

                                <br/><br/>

                                Additional Information

                                <br/><br/>

                                Are you a game changer and want to shape the future with us? Apply now online.

                                <br/><br/>

                                APPLY NOW

                                <br/><br/>

                                Interested? Join us now! In case of any further questions please feel free to contact Tatiana Sheremet via Tatiana.Sheremet@rolandberger.com.
                            </div>
                        }
                        links={[
                            "Надсилай своє резюме на пошту та отримуй тест: Tatiana.Sheremet@rolandberger.com",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                </div>

                <div className="row margin-top-20">
                    <CardTest
                        image={"test-roland"}
                        name={"Roland Berger"}
                        description={"Подавайся на вакансію Consulting – Internship та отримуй тест."}
                        longDescription={
                            <div>
                                <b>Consulting – Internship</b>

                                <br/><br/>

                                Intern

                                <br/><br/>

                                During a period of 6 months, you will become a team member on one of our projects for a top European company. Assignments are very varied. Projects can relate to devising market, production or sourcing strategies or to redesigning organizations. Among other things, your role can be to interview project stakeholders, prepare meetings, analyze obtained information, extract key learning and take fact based decisions.

                                <br/><br/>

                                Qualifications

                                <br/><br/>

                                The people who fit most easily into our culture are bright, proactive, hard-working, unpretentious, confident, personable and have a sense of humor. Our candidates are 5th year students or recent graduates with 1 year experience or 1-2 internships in relevant areas and have excellent oral and written communication skills in Ukrainian and English. Please note that a strong emphasis is placed on analytical skills.

                                <br/><br/>

                                Additional Information

                                <br/><br/>

                                Are you a game changer and want to shape the future with us? Apply now online.

                                <br/><br/>

                                APPLY NOW

                                <br/><br/>

                                We hire interns during the academic year based on available opportunities related to projects. If you are interested, please submit your application by indicating in your cover letter the period of the year that you are considering. We will contact you if an opportunity arises.
                            </div>
                        }
                        links={[
                            "Подавай заявку, надсилай резюме на пошту та отримуй тест: Tatiana.Sheremet@rolandberger.com",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                    <CardTest
                        image={"gift-genesis"}
                        name={"Genesis"}
                        description={"Не втрачай можливості та проходь перший етап відбору у Genesis IT School 10.0"}
                        longDescription={`Згодом ми надамо посилання на перший етап відбору у школу - математичний тест.`}
                        links={[
                            "Лінк на тест: https://www.classmarker.com/online-test/start/?quiz=dtn5f6fa78187c6e",
                        ]}
                        standLink={"https://youtu.be/W7ipkmhsVPM"}
                    />
                </div>
            </div>

            <div className="container-fluid background-footer">
                <div className="row">
                    <div className="container">
                        <div className="row">
                            <div className="col-12 col-lg-6 no-gutters-desktop">
                                <p className="footer-words">© Highers, JCC NaUKMA, 2020</p>
                            </div>
                            <div className="col-lg-6 justify-content-end row-desktop no-gutters">
                                <p className="footer-words">Big Career Fest. Online</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export {
    BlockTests as default
};
