import React, {useState} from 'react';

import {
    useMediaQuery
} from "react-responsive";

import { Element } from "react-scroll/modules";

import { InputTextField } from "../../genesis/inputs/InputText";

import Alert from "@material-ui/lab/Alert/Alert";
import Snackbar from "@material-ui/core/Snackbar/Snackbar";

import BlockWinners from "./BlockWinners";

import axios from "axios";

import * as ReactGA from "react-ga";

const POST_REGISTRATION_REQUEST = "/registration/metinvest-game";

const BlockGame = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    const [state, setState] = useState({
        // Error controllers
        errorStatus: null,
        errorFields: [],
        isSuccess: false,
    });

    const [application, setApplication] = useState({
        // User data
        nsp: null,
        email: null,
        // Boolean triggers
        isConfirmed: true,
    });

    const getInitialState = () => {
        return {
            isError: false,
            isRegistered: false,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const asyncRegistrationRequest = async (params) => {
        try {
            const response = await axios.post(POST_REGISTRATION_REQUEST, {
                ...application,
                ...params
            });
            return response.data;
        } catch (err) {
            switch (err.response.status) {
                case 403:
                    setState({
                        ...state,
                        isError: true,
                        isRegistered: false,
                        errorMsg: err.response.data.data.errors[0].msg,
                        errorStatus: err.response.status,
                        errorFields: err.response.data.data.errors
                            .map(errorObj => errorObj.param),
                    });
                    break;

                case 409:
                    setState({
                        ...state,
                        isError: false,
                        isRegistered: true,
                    });
                    break;

                case 500:
                    setState({
                        ...state,
                        isRegistered: false,
                        errorStatus: err.response.status,
                        errorFields: [],
                    });
                    break;
            }
            throw err;
        }
    };

    const onChangeApplication = evt => {
        setApplication({
            ...application,
            [evt.target.name]: evt.target.value
        });
    };

    const onClickRegistration = (evt) => {
        evt.preventDefault();
        /* Nullify the error state */
        setState({
            ...state,
            ...getInitialState()
        });
        /* Edge case of non-confirmation of user data */
        if (!application.isConfirmed)
            return;
        /* Post the genesis data into the database */
        (async () => {
            const data = await asyncRegistrationRequest();
            if (data.isSuccess) {
                // Trigger Google Analytics registration click
                ReactGA.event({
                    category: 'Landing Genesis',
                    action: 'Submit Registration',
                    label: 'Block Registration',
                });
                // Initialize new empty state
                setState({
                    ...state,
                    ...data,
                    ...getInitialState(),
                    isRegistered: true
                });
            }
        })();
    };

    const onCloseAlert = (evt) => {
        setState({
            ...state,
            ...getInitialState()
        });
    };

    if (isTabletOrMobileDevice)
        return (
            <Element
                id="metinvest-game"
                name="metinvest-game"
                className="element">
                <div className="container-fluid padding-tb-40">
                    <Snackbar
                        open={state.isError}
                        autoHideDuration={6000}
                        onClose={null}>
                        <Alert
                            onClose={onCloseAlert}
                            severity="error">
                            {state.errorMsg}
                        </Alert>
                    </Snackbar>

                    <div className="col-12 col-lg-12">
                        <div className="row">
                            <div className="col-12 text-center">
                                <span className="landing-metinvest-header-mobile font-weight-500">Онлайн-гра "Залізна логіка"</span>
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <span className="landing-metinvest-game-subheader font-weight-400">
                                    Вивчай гірничо-металургійну галузь і проходь весь виробничий ланцюжок із квестами і завданнями в інтерактивній навчальній грі!
                                </span>
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <span className="landing-metinvest-game-subheader font-weight-300">
                                    Реєструйся, вивчай, грай - та вигравай круті брендовані набори Metinvest. Шукай себе у списках переможців 28/09/2020 під грою!
                                </span>
                            </div>
                        </div>

                        <div className="row margin-top-40">
                            <div className="col-12 text-center">
                                {
                                    state.isRegistered
                                        ? (
                                            <iframe
                                                className="landing-metinvest-game-iframe-mobile"
                                                src="https://steellogic.metinvestholding.com/">
                                            </iframe>
                                        ) : (
                                            <div className="container-fluid landing-metinvest-game-frame-mobile">
                                                <div className="row">
                                                    <div className="col-12">
                                                        <p className="label-form-genesis font-weight-500">
                                                            Напиши своє ПІБ
                                                            <div className="required-mark">*</div>
                                                        </p>
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col-12">
                                                        <InputTextField
                                                            name={"nsp"}
                                                            type="text"
                                                            value={application.nsp}
                                                            className={"genesis-input"}
                                                            placeholder="Петренко Іван Юрійович"
                                                            onChange={onChangeApplication}
                                                            errorStatus={state.errorStatus}
                                                            errorFields={state.errorFields}
                                                        />
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col-12">
                                                        <p className="label-form-genesis">
                                                        <span className="font-weight-500">
                                                            Напиши свою електронну пошту
                                                        </span>
                                                            <div className="required-mark">*</div>
                                                        </p>
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col-12">
                                                        <InputTextField
                                                            name={"email"}
                                                            type="email"
                                                            value={application.email}
                                                            className={"genesis-input"}
                                                            placeholder="example@com"
                                                            onChange={onChangeApplication}
                                                            errorStatus={state.errorStatus}
                                                            errorFields={state.errorFields}
                                                        />
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col-12">
                                                        {(() => {
                                                            if (state.isSuccess)
                                                                return (
                                                                    <p className="thank-you-genesis">
                                                                        Дякуємо! Чекай від нас новин :)
                                                                    </p>
                                                                );

                                                            return (
                                                                <button
                                                                    type="submit"
                                                                    className={(() => {
                                                                        if (application.isConfirmed) {
                                                                            return "submit-button-genesis font-weight-500"
                                                                        } else {
                                                                            return "submit-button-genesis disabled font-weight-500"
                                                                        }
                                                                    })()}
                                                                    onClick={onClickRegistration}>
                                                                    Почати грати
                                                                </button>
                                                            );
                                                        })()}
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                }
                            </div>
                        </div>

                        <div className="row margin-top-40">
                            <div className="col-12">
                                <BlockWinners/>
                            </div>
                        </div>
                    </div>
                </div>
            </Element>
        );

    return (
        <Element
            id="metinvest-game"
            name="metinvest-game"
            className="element">
            <div className="container-fluid padding-tb-60">
                <Snackbar
                    open={state.isError}
                    autoHideDuration={6000}
                    onClose={null}>
                    <Alert
                        onClose={onCloseAlert}
                        severity="error">
                        {state.errorMsg}
                    </Alert>
                </Snackbar>

                <div className="col-12 col-lg-12">
                    <div className="row">
                        <div className="col-2"/>
                        <div className="col-8 text-center">
                            <span className="landing-metinvest-game-header font-weight-600">Онлайн-гра "Залізна логіка"</span>
                        </div>
                        <div className="col-2"/>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-2"/>
                        <div className="col-8 text-center">
                            <p className="landing-metinvest-game-subheader font-weight-400">
                                Вивчай гірничо-металургійну галузь і проходь весь виробничий ланцюжок із квестами і завданнями в інтерактивній навчальній грі!
                            </p>

                            <p className="landing-metinvest-game-subheader font-weight-300">
                                Реєструйся, вивчай, грай - та вигравай круті брендовані набори Metinvest. Шукай себе у списках переможців 28/09/2020 під грою!
                            </p>
                        </div>
                        <div className="col-2"/>
                    </div>

                    <div className="row margin-top-40">
                        <div className="col-12 text-center">
                            {
                                state.isRegistered
                                    ? (
                                        <iframe
                                            className="landing-metinvest-game-iframe"
                                            src="https://steellogic.metinvestholding.com/">
                                        </iframe>
                                    ) : (
                                        <div className="container-fluid landing-metinvest-game-frame">
                                            <div className="row">
                                                <div className="col-12">
                                                    <p className="label-form-genesis font-weight-500">
                                                        Напиши своє ПІБ
                                                        <div className="required-mark">*</div>
                                                    </p>
                                                </div>
                                            </div>

                                            <div className="row">
                                                <div className="col-12">
                                                    <InputTextField
                                                        name={"nsp"}
                                                        type="text"
                                                        value={application.nsp}
                                                        className={"genesis-input"}
                                                        placeholder="Петренко Іван Юрійович"
                                                        onChange={onChangeApplication}
                                                        errorStatus={state.errorStatus}
                                                        errorFields={state.errorFields}
                                                    />
                                                </div>
                                            </div>

                                            <div className="row">
                                                <div className="col-12">
                                                    <p className="label-form-genesis">
                                                        <span className="font-weight-500">
                                                            Напиши свою електронну пошту
                                                        </span>
                                                        <div className="required-mark">*</div>
                                                    </p>
                                                </div>
                                            </div>

                                            <div className="row">
                                                <div className="col-12">
                                                    <InputTextField
                                                        name={"email"}
                                                        type="email"
                                                        value={application.email}
                                                        className={"genesis-input"}
                                                        placeholder="example@com"
                                                        onChange={onChangeApplication}
                                                        errorStatus={state.errorStatus}
                                                        errorFields={state.errorFields}
                                                    />
                                                </div>
                                            </div>

                                            <div className="row">
                                                <div className="col-12">
                                                    {(() => {
                                                        if (state.isSuccess)
                                                            return (
                                                                <p className="thank-you-genesis">
                                                                    Дякуємо! Чекай від нас новин :)
                                                                </p>
                                                            );

                                                        return (
                                                            <button
                                                                type="submit"
                                                                className={(() => {
                                                                    if (application.isConfirmed) {
                                                                        return "submit-button-genesis font-weight-500"
                                                                    } else {
                                                                        return "submit-button-genesis disabled font-weight-500"
                                                                    }
                                                                })()}
                                                                onClick={onClickRegistration}>
                                                                Почати грати
                                                            </button>
                                                        );
                                                    })()}
                                                </div>
                                            </div>
                                        </div>
                                    )
                            }
                        </div>
                    </div>

                    <div className="row margin-top-40">
                        <div className="col-12">
                            <BlockWinners/>
                        </div>
                    </div>
                </div>
            </div>
        </Element>
    );
};

export {
    BlockGame as default
};
