import React, { useState } from 'react';

import * as ReactGA from "react-ga";


const ContentMore = ({
                        description
                    }) => {
    const restriction = 90;

    const [expanded, setExpanded] = useState(false);

    const divClassName
        = expanded
            ? 'expanded'
            : 'collapsed';

    const divClassNameButton
        = expanded
            ? 'no-background-overflow'
            : 'background-overflow';

    if (description && description.length <= restriction)
        return (
            <div className={`text-vacancy-no-overflow`}>
                {description}
            </div>
        );

    const content =
        description.slice(0, restriction);

    return (
        <div className="text-center">
            <div
                className={`text-vacancy-new-overflow ${divClassName}`}>
                {!expanded ? `${content}...` : description}
                <button
                    onClick={() => setExpanded(!expanded)}
                    className="button-expand-text-vacancy font-weight-500">
                    {!expanded ? 'Показати більше' : null}
                </button>
            </div>

            <div
                onClick={() => setExpanded(!expanded)}
                className={`div-expand-text-vacancy ${divClassNameButton}`}
            >
                <button
                    className="button-expand-text-vacancy">
                    {expanded ? 'Сховати' : null}
                </button>
            </div>
        </div>
    );
};

class CardOrganizators extends React.Component {
    render() {
        return (
            <div className="col-12 col-lg-5 margin-top-30">
                <div className="row row-icon-block">
                    <div className="col-12 row-center">
                        <img
                            src={this.props.image}
                            className="organizators-block"
                            alt={"organizators-image"}
                        />
                    </div>
                </div>

                <div className="row">
                    <div className="col-12">
                        <p className="title-small-block font-weight-500">{this.props.title}</p>
                    </div>
                </div>

                <div className="row">
                    <div className="col-12">
                        <p className="subtitle-small-block font-weight-500">{this.props.subTitle}</p>
                    </div>
                </div>

                <div className="row">
                    <div className="col-12">
                        <p className="title-small-block-text-org font-weight-400">
                            <ContentMore
                                description={this.props.explanation}/>
                        </p>
                    </div>
                </div>

                <div className="row">
                    <div className="col-12 row-center know-more-bottom">
                        <a
                            target="_blank"
                            href={this.props.buttonLink}
                            id={"width-button"}>
                            <button
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: `Click Organizators / ${this.props.title}`,
                                        label: 'Block Organizators',
                                    });
                                }}
                                className={`${this.props.buttonClass} font-weight-500`}>
                                Telegram канал
                            </button>
                        </a>
                    </div>
                </div>
            </div>
        );
    }
}

export {
    CardOrganizators as default
};
