import React, { useState } from "react";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
    Redirect,
} from "react-router-dom";

import {
    Element
} from "react-scroll/modules";

import {
    Animated
} from "react-animated-css";

import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from "@material-ui/core/Snackbar/Snackbar";

import {
    BlockResume,
    BlockSocialNetworks
} from "./BlockEntities";

// Import Google Firebase modules
import {
    storage,
    ResumesBCF
} from '../../../../../configs/google/firebase/config';

import * as ReactGA from "react-ga";

import axios from "axios";

const POST_REGISTRATION_REQUEST = '/registration/bcf';


const delay = ms => new Promise(res => setTimeout(res, ms));


const Alert = (props) => {
    return <MuiAlert
        elevation={6}
        variant="filled"
        {...props} />;
};


const BlockRegistration = (props) => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    const [state, setState]
        = useState({
            application: {
                email: null,
                file: {
                    name: null,
                    data: null
                },
            },
            // Boolean triggers
            isError: false,
            isConfirm: true,
            isUploaded: false,
            // Error state controllers
            redirect: null,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        });

    const getInitialState = () => {
        return {
            isError: false,
            redirect: null,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onSelectFile = () => {
        const selector = document.createElement('input');
        selector.setAttribute('type', 'file');
        selector.setAttribute('multiple', 'multiple');
        selector.onchange
            = evt => {
                setState({
                    ...state,
                    application: {
                        ...state.application,
                        file: evt.target.files[0]
                    },
                    isUploaded: true
                });
            };
        return selector;
    };

    const fileSelector = onSelectFile();

    const asyncRegistrationRequest = async () => {
        try {
            const response = await axios.post(POST_REGISTRATION_REQUEST, {
                ...state.application
            });
            return response.data;
        } catch (err) {
            switch (err.response.status) {
                case 403:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: err.response.data.data.errors[0].msg,
                        errorStatus: err.response.status,
                        errorFields: err.response.data.data.errors
                            .map(errorObj => errorObj.param),
                    });
                    break;

                case 409:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: "Ви вже реєстувались :3",
                        errorStatus: err.response.status,
                        errorFields: ["email"]
                    });
                    break;

                case 500:
                    setState({
                        ...state,
                        isError: true,
                        errorStatus: err.response.status,
                        redirect: '/500'
                    });
                    break;
            }
            throw err;
        }
    };

    const onChangeApplication
        = (evt) => {
            setState({
                ...state,
                application: {
                    ...state.application,
                    [evt.target.name]: evt.target.value
                }
            });
        };

    const onClickFileUpload = (evt) => {
        evt.preventDefault();
        fileSelector.click();
        // Trigger click resume event
        ReactGA.event({
            category: 'Landing BCF',
            action: 'Click Resume',
            label: 'Block Registration'
        });
    };

    const onClickFileRemove = (evt) => {
        setState({
            ...state,
            isUploaded: false,
            application: {
                ...state.application,
                file: {
                    name: null,
                    data: null
                }
            }
        });
    };

    const onClickConfirmation = (evt) => {
        setState({
            ...state,
            isConfirm: !state.isConfirm
        });
    };

    const onClickRegistration = (evt) => {
        (async () => {
            const data = await asyncRegistrationRequest();
            if (data.isSuccess) {
                setState({
                    ...state,
                    ...data,
                    ...getInitialState(),
                });
                // Trigger event registration
                ReactGA.event({
                    category: 'Landing BCF',
                    action: 'Submit Registration',
                    label: 'Block Registration',
                });
                /* If user not uploaded file - end function */
                if (!state.isUploaded) {
                    // Redirect to the Telegram Channel
                    await delay(1000);
                    window.location.replace("https://rebrand.ly/nrlflkb");
                    return;
                }
                /* If application has file, upload it in Firebase */
                if (state.isUploaded) {
                    const
                        timeString = new Date().toISOString(),
                        fileName = state.application.file.name,
                        fileNameFirebase = `${state.application.email}-${timeString}-${fileName}`,
                        uploadTask = storage
                            .ref(ResumesBCF)
                            .child(fileNameFirebase)
                            .put(state.application.file);
                    /* Upload image in the cloud */
                    uploadTask.on(
                        'state_changed',
                        async snapshot => {
                        },
                        err => console.log(err),
                        complete => {
                            storage
                                .ref(ResumesBCF)
                                .child(fileNameFirebase)
                                .getDownloadURL()
                                .then(url => {
                                    // Redirect to the Telegram Channel
                                    window.location.replace("https://rebrand.ly/nrlflkb");
                                });
                        },
                    );
                }
            }
        })();
    };

    const onCloseAlert = (evt) => {
        setState({
            ...state,
            ...getInitialState()
        });
    };

    if (state.redirect)
        return (
            <Redirect
                external
                target={"_blank"}
                to={"https://rebrand.ly/nrlflkb"}
            />
        );

    return (
        <Element
            id="registration"
            name="registration"
            className="element">
            <div
                className={
                    isTabletOrMobileDevice
                        ? "container-fluid block-registration padding-bottom-30 margin-top-30"
                        : "container-fluid block-registration padding-bottom-30"
                }>
                <div className="row">
                    <div className="container">
                        <div
                            className={
                                isTabletOrMobileDevice
                                    ? "row margin-top-60"
                                    : "row margin-top-60"
                            }>
                            <div className="col-12">
                                <p
                                    className={
                                        isTabletOrMobileDevice
                                            ? "landing-registration-title-mobile font-weight-600"
                                            : "landing-registration-title font-weight-600"
                                    }>
                                    Реєстрація
                                </p>
                            </div>
                        </div>

                        <Snackbar
                            open={state.isError}
                            autoHideDuration={6000}
                            onClose={null}>
                            <Alert
                                onClose={onCloseAlert}
                                severity="error">
                                {state.errorMsg}
                            </Alert>
                        </Snackbar>

                        <Element
                            id="bcfRegistration"
                            name="bcfRegistration"
                            className="element">
                            {(() => {
                                if (!state.isSuccess)
                                    return (
                                        <div className="row row-center padding-top-organizators">
                                            <div className="col-12 col-md-10 col-lg-8">
                                                <div className="col-12 margin-bottom-30">
                                                    <div className="row">
                                                        <div className="col-12 col-lg-12">
                                                            <p className="label-form-genesis font-weight-500">
                                                                Твій e-mail
                                                                <div className="required-mark">
                                                                    <b>*</b>
                                                                </div>
                                                            </p>
                                                        </div>
                                                    </div>

                                                    <div className="row">
                                                        <div className="col-12 col-lg-12">
                                                            <input
                                                                name={"email"}
                                                                type="email"
                                                                value={state.application.email}
                                                                className={(() => {
                                                                    if (state.errorFields.includes('email') && state.errorStatus >= 400)
                                                                        return 'landing-registration-input required-field';

                                                                    return 'landing-registration-input';
                                                                })()}
                                                                placeholder="example@com"
                                                                onChange={onChangeApplication}
                                                            />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="col-12 margin-bottom-20 mt-3">
                                                    <div className="row">
                                                        <div className="col-12">
                                                            <div className="row">
                                                                <div className="col-12 col-lg-12">
                                                                    <p className="label-form-genesis font-weight-400">
                                                                        <span className="font-weight-500">Резюме</span> (це можливість потрапити у базу компаній та отримати роботу, поле необов’язкове)
                                                                    </p>
                                                                </div>
                                                            </div>

                                                            <div className="row">
                                                                <div className="col-12 col-12-lg">
                                                                    <BlockResume
                                                                        isUploaded={state.isUploaded}
                                                                        fileName={state.application.file.name}
                                                                        onClickFileRemove={onClickFileRemove}
                                                                        onClickFileUpload={onClickFileUpload}
                                                                        isTabletOrMobileDevice={isTabletOrMobileDevice}
                                                                    />
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>

                                                    <div className="row margin-top-20">
                                                        <div className="col-12 col-lg-12">
                                                            <div className="form-group form-check">
                                                                <input
                                                                    id="checkboxConfirmation"
                                                                    name={"isConfirm"}
                                                                    type="checkbox"
                                                                    className="form-check-input active"
                                                                    checked={state.isConfirm}
                                                                    onChange={onClickConfirmation}
                                                                />
                                                                <label className="form-check-label" htmlFor="checkboxConfirmation">
                                                                    Надаю згоду на {' '}
                                                                    <a
                                                                        target={"_blank"}
                                                                        href="https://bit.ly/2Yu3Vdl">
                                                                        обробку персональних даних
                                                                    </a>
                                                                </label>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="col-12 mt-3">
                                                    <button
                                                        type="submit"
                                                        onClick={
                                                            state.isConfirm
                                                                ? onClickRegistration
                                                                : null
                                                        }
                                                        className={
                                                            state.isConfirm
                                                                ? "landing-registration-button-registration font-weight-500"
                                                                : "landing-registration-button-registration font-weight-500 disabled"
                                                        }>
                                                        Реєстрація
                                                    </button>
                                                </div>

                                                <BlockSocialNetworks
                                                    isTabletOrMobileDevice={isTabletOrMobileDevice}
                                                />
                                            </div>
                                        </div>
                                    );

                                return (
                                    <div className="row row-center padding-top-organizators">
                                        <div className="col-12 col-md-10 col-lg-8">
                                            <div className="row">
                                                <div className="col-12 col-lg-12 row-center">
                                                    <Animated
                                                        animationIn="fadeIn"
                                                        animationOut="fadeOut"
                                                        animationInDuration={400}
                                                        animationOutDuration={400}
                                                        isVisible={true}>
                                                        <p className="thank-you">
                                                            Дякуємо! Перевір пошту :3
                                                        </p>
                                                    </Animated>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                );
                            })()}
                        </Element>
                    </div>
                </div>
            </div>
        </Element>
    );
};


export {
    BlockRegistration as default
};
