import React from "react";


const InputTextField = ({
                            name,
                            type,
                            value,
                            className,
                            placeholder,
                            onChange,
                            errorStatus,
                            errorFields,
                        }) => {
    return (
        <input
            name={name}
            type={type}
            value={value}
            className={(() => {
                if (
                    (errorFields.includes(name)) &&
                    (errorStatus >= 400))
                    return `${className} required-field`;

                return className;
            })()}
            placeholder={placeholder}
            onChange={onChange}
        />
    );
};

const InputTextArea = ({
                            name,
                            rows,
                            value,
                            className,
                            placeholder,
                            onChange,
                            errorStatus,
                            errorFields,
                        }) => {
    return (
        <textarea
            name={name}
            rows={rows}
            value={value}
            className={(() => {
                if (
                    (errorFields.includes(name)) &&
                    (errorStatus >= 400))
                    return `${className} required-field`;

                return className;
            })()}
            placeholder={placeholder}
            onChange={onChange}
        />
    );
};


export {
    InputTextField,
    InputTextArea
};
