import React, {useState} from "react";

import {
    useMediaQuery
} from "react-responsive";

import { Element } from "react-scroll/modules";

import Alert from "@material-ui/lab/Alert/Alert";
import Snackbar from "@material-ui/core/Snackbar/Snackbar";

import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';

import {
    InputTextArea,
    InputTextField
} from "../../genesis/inputs/InputText";

import * as ReactGA from "react-ga";

import {
    ResumesMetinvest,
    storage
} from "../../../../configs/google/firebase/config";

import axios from "axios";

const POST_REGISTRATION_REQUEST = "/registration/metinvest";


const BlockRegistration = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    const [state, setState] = useState({
        // Error controllers
        errorStatus: null,
        errorFields: [],
        isSuccess: false,
    });

    const [application, setApplication] = useState({
        // Boolean triggers
        isUploaded: false,

        // User data
        nsp: null,
        email: null,
        phone: null,
        region: "Київ",
        sphere: "Інформаційні технології",
        institute: "НУ Києво-Могилянська академія",
        specialization: null,

        file: null,
        fileName: null,
        fileData: null,

        confirm: true,
    });

    const getInitialState = () => {
        return {
            isError: false,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onSelectFile = () => {
        const selector = document.createElement('input');
        selector.setAttribute('type', 'file');
        selector.setAttribute('multiple', 'multiple');
        selector.onchange = evt =>
            setApplication({
                ...application,
                file: evt.target.files[0],
                isUploaded: true
            });
        return selector;
    };

    const fileSelector = onSelectFile();

    const asyncRegistrationRequest = async (params) => {
        try {
            const response = await axios.post(POST_REGISTRATION_REQUEST, {
                ...application,
                ...params
            });
            return response.data;
        } catch (err) {
            switch (err.response.status) {
                case 403:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: err.response.data.data.errors[0].msg,
                        errorStatus: err.response.status,
                        errorFields: err.response.data.data.errors
                            .map(errorObj => errorObj.param),
                    });
                    break;

                case 409:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: "Ви вже реєстувались ;3",
                        errorStatus: err.response.status,
                        errorFields: ["email"],
                    });
                    break;

                case 500:
                    setState({
                        ...state,
                        errorStatus: err.response.status,
                        errorFields: [],
                    });
                    break;
            }
            throw err;
        }
    };

    const onChangeApplication = evt => {
        setApplication({
            ...application,
            [evt.target.name]: evt.target.value
        });
    };

    const onClickFile = async (evt) => {
        evt.preventDefault();
        await fileSelector.click();
        // Trigger click resume event
        ReactGA.event({
            category: 'Landing Metinvest',
            action: 'Click Resume',
            label: 'Block Registration'
        });
    };

    const onClickConfirmation = () => {
        setApplication({
            ...application,
            confirm: !application.confirm
        });
    };

    const onClickRegistration = (evt) => {
        evt.preventDefault();
        /* Nullify the error state */
        setState({
            ...state,
            ...getInitialState()
        });
        /* Edge case of non-confirmation of user data */
        if (!application.confirm)
            return;
        /* If application has file, upload it in Firebase */
        if (application.file) {
            const
                timeString = new Date().toISOString(),
                fileName = application.file.name,
                fileNameFirebase = `${application.nsp}-${timeString}-${fileName}`,
                uploadTask = storage
                    .ref(ResumesMetinvest)
                    .child(fileNameFirebase)
                    .put(application.file);
            /* Upload image in the cloud */
            uploadTask.on(
                'state_changed',
                async snapshot => {},
                err => console.log(err),
                complete => {
                    storage
                        .ref(ResumesMetinvest)
                        .child(fileNameFirebase)
                        .getDownloadURL()
                        .then(url => {
                            /* Post the genesis data into the database */
                            (async () => {
                                const data = await asyncRegistrationRequest({ fileData: url });
                                if (data.isSuccess) {
                                    // Trigger Google Analytics registration click
                                    ReactGA.event({
                                        category: 'Landing Genesis',
                                        action: 'Submit Registration',
                                        label: 'Block Registration',
                                    });
                                    // Initialize new empty state
                                    setState({
                                        ...state,
                                        ...data,
                                        ...getInitialState()
                                    });
                                }
                            })();
                        });
                },
            );
        } else {
            /* Post the genesis data into the database */
            (async () => {
                const data = await asyncRegistrationRequest();
                if (data.isSuccess) {
                    // Trigger Google Analytics registration click
                    ReactGA.event({
                        category: 'Landing Genesis',
                        action: 'Submit Registration',
                        label: 'Block Registration',
                    });
                    // Initialize new empty state
                    setState({
                        ...state,
                        ...data,
                        ...getInitialState()
                    });
                }
            })();
        }
    };

    const onCloseAlert = (evt) => {
        setState({
            ...state,
            ...getInitialState()
        });
    };

    return (
        <Element
            id="metinvest-registration"
            name="metinvest-registration"
            className="element">
            <div className={
                isTabletOrMobileDevice
                    ? "container background-white padding-tb-40"
                    : "container background-white padding-tb-60"
            }>
                <div className="row">
                    <div className="col-12 text-center">
                        <p
                            className={
                                isTabletOrMobileDevice
                                    ? "landing-metinvest-header-mobile font-weight-500"
                                    : "landing-metinvest-registration-header font-weight-600"
                            }>
                            {
                                isTabletOrMobileDevice
                                    ? "Працюй у Metinvest вже зараз!"
                                    : "Працевшлаштовуйся прямо зараз!"
                            }
                        </p>
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12 text-center">
                        <p className="landing-metinvest-registration-subheader">
                            Якщо ти цікавишся працевлаштуванням у нашій компанії, заповни невелику форму, щоб ми могли зв'язатися з тобою :)
                        </p>
                    </div>
                </div>

                <Snackbar
                    open={state.isError}
                    autoHideDuration={6000}
                    onClose={null}>
                    <Alert
                        onClose={onCloseAlert}
                        severity="error">
                        {state.errorMsg}
                    </Alert>
                </Snackbar>

                <div className="row row-center margin-top-40">
                    <div className="col-12 col-md-12 col-lg-8">
                        <form>
                            <div className="row">
                                <div className="col-12 col-lg-12">
                                    <p className="label-form-genesis font-weight-500">
                                        Напиши своє ПІБ
                                        <div className="required-mark">*</div>
                                    </p>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-12 col-lg-12">
                                    <InputTextField
                                        name={"nsp"}
                                        type="text"
                                        value={application.nsp}
