import React from 'react';
import { Link } from 'react-scroll';
import education1 from '../images/education-1.png'
import education2 from '../images/education-2.png'
import education3 from '../images/education-3.png'
import ShortTextEducation from "./shortTextEducation";
import LongTextEducation from "./longTextEducation";

class EducationParts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            openLongHour: true,
            openLongLectures: false,
            openLongTests: false
        };
        this.onClickHours = this.onClickHours.bind(this);
        this.onClickLectures = this.onClickLectures.bind(this);
        this.onClickTests = this.onClickTests.bind(this);
    }

    onClickHours(){
        this.setState({
            openLongHour: !this.state.openLongHour
        })
    }

    onClickLectures(){
        this.setState({
            openLongLectures: !this.state.openLongLectures
        })
    }

    onClickTests(){
        this.setState({
            openLongTests: !this.state.openLongTests
        })
    }

    render() {
        return (
            <div className="container-fluid education-parts-container">
                <div className="row">
                    <div className="container">
                        <div className="row center-row">
                            <div className="col-8 center-row">
                                <p className="title-block-text">Як побудоване навчання?</p>
                            </div>
                        </div>
                        <div className="row center-row">
                            <div className="col-8 center-row">
                                <p className="title-block-secondary-text">
                                    Навчання складається з трьох етапів:
                                </p>
                            </div>
                        </div>

                        <div className="row center-row">
                            <div className="col-4" onClick={this.onClickHours}>
                                {
                                    this.state.openLongHour ?
                                         <div className="education-structure-active-subblock">
                                            {/*<div className="image-education-block">*/}
                                            {/*    <img src={education1} className="image-education"/>*/}
                                            {/*</div>*/}
                                            <p className="title-text-subblock-active">
                                                Година з компанією - youtube-трансляція з представниками компанії
                                            </p>
                                            <p className="secondary-text-subblock">
                                                Дізнавайся про те:
                                                <ul>
                                                    <li>чим займається компанія,</li>
                                                    <li>які умови праці пропонує,</li>
                                                    <li>як буде відбуватись робочий процес,</li>
                                                    <li>як проходить відбір співробітників,</li>
                                                    <li>які можливості пропонує компанія для отримання першого досвіду</li>
                                                </ul>
                                            </p>
                                        </div>

                                        :
                                        <ShortTextEducation
                                            title={"Година з компанією"}
                                            shortDescription={"Аналіз роботодавця та правильна презентація себе для працевлаштування."}
                                            image={education1}
                                            imageClass={"image-education"}
                                        />
                                }
                            </div>
                            <div className="col-4" onClick={this.onClickLectures}>
                                {
                                    this.state.openLongLectures ?
                                        <LongTextEducation
                                            title={"Добірки hard-skills лекцій для працевлаштування"}
                                            shortDescription={"Лекції від представників компаній, об’єднані за сферами. Hard-skills, які дійсно необхідні майбутнім працедавцям + сертифікат."}
                                            image={education2}
                                            imageClass={"image-education-2"}
                                        />
                                        :
                                        <ShortTextEducation
                                            title={"Добірки hard-skills лекцій для працевлаштування"}
                                            shortDescription={"Лекції від представників компаній, об’єднані за сферами. Hard-skills, які дійсно необхідні майбутнім працедавцям + сертифікат."}
                                            image={education2}
                                            imageClass={"image-education-2"}
                                        />
                                }
                            </div>

                            <div className="col-4" onClick={this.onClickTests}>
                                {
                                    this.state.openLongTests ?
                                        <LongTextEducation
                                            title={"Тестування як перший етап відбору і вакансії"}
                                            shortDescription={"Застосування отриманих знань на практиці та подача на вакансії для студентів"}
                                            image={education3}
                                            imageClass={"image-education"}
                                        />
                                        :
                                        <ShortTextEducation
                                            title={"Тестування як перший етап відбору і вакансії"}
                                            shortDescription={"Застосування отриманих знань на практиці та подача на вакансії для студентів"}
                                            image={education3}
                                            imageClass={"image-education"}
                                        />
                                }
                            </div>
                        </div>

                        <div className="row center-row">
                            <div className="col-6 column-not-main-button center-row">
                                <Link activeClass="active" className="test1" to="registration" spy={true} smooth={true} duration={500} >
                                    <button className="button-main-registrate">Реєстрація</button>
                                </Link>
                            </div>
                        </div>
                        <div className="row center-row">
                            <div className="col-4 center-row">
                                <p className="details-registration-education-text">
                                    Доступ до навчання відкриється зареєстрованим учасникам в дні заходу
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default EducationParts;
