import React from 'react';

import 'react-tabs/style/react-tabs.css';

import srcX from '../../../../../images/icons/modified/x.svg';
import srcXBlack from '../../../../../images/icons/modified/x_black.svg';

import {
    Animated
} from "react-animated-css";

import Popup from "reactjs-popup";


const PopUpBook = ({
                       src,
                       title,
                       author,
                       description,
                       isTabletOrMobileDevice
                   }) => {
    return (
        <Popup
            modal
            disabled={true}
            lockScroll={true}
            closeOnDocumentClick
            trigger={
                <div
                    className="landing-metinvest-books-card-frame">
                    <div className="row">
                        <div className="col-12 text-center">
                            <img
                                src={src}
                                className={
                                    isTabletOrMobileDevice
                                        ? "landing-metinvest-books-image-mobile"
                                        : "landing-metinvest-books-image"
                                }
                                alt="Книга, Топ-10, Metinvest"
                            />
                        </div>
                    </div>

                    <div className="container-fluid mt-2">
                        <div className="row">
                            <div className={
                                isTabletOrMobileDevice
                                    ? "col-12 text-center"
                                    : "col-12 text-left"
                            }>
                                <span className="landing-metinvest-books-card-title font-weight-500">
                                    {title}
                                </span>
                            </div>
                        </div>

                        <div className="row margin-top-10">
                            <div className={
                                isTabletOrMobileDevice
                                    ? "col-12 text-center"
                                    : "col-12 text-left"
                            }>
                                <span className="landing-metinvest-books-card-author">
                                    {author}
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            }
        >
            {close => (
                <div
                    className="container-fluid landing-metinvest-books-popup-frame">
                    <a
                        className="close row-mobile"
                        onClick={close}>
                        <img
                            src={srcXBlack}
                            className="close-icon"
                            alt={"close-popup"}
                        />
                    </a>
                    <a
                        className="close row-desktop"
                        onClick={close}>
                        <img
                            src={srcX}
                            className="close-icon"
                            alt={"close-popup"}
                        />
                    </a>

                    <div className="row">
                        <div className="col-4">
                            <img
                                src={src}
                                className="close-icon"
                                alt={"close-popup"}
                            />
                        </div>

                        <div className="col-8">
                            <div className="row margin-top-20">
                                <div className="col-12">
                                    <span className="landing-metinvest-books-popup-author">
                                        {author}
                                    </span>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-12">
                                    <span className="landing-metinvest-books-popup-title">
                                        {title}
                                    </span>
                                </div>
                            </div>

                            <div className="row margin-top-20">
                                <div className="col-12">
                                    <span className="landing-metinvest-books-popup-description">
                                        {description}
                                    </span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </Popup>
    );
};

export {
    PopUpBook as default
};
