import React from 'react';

import srcArrow from "../../../../../images/landings/metinvest/arrows/arrow.svg"

const CardArticle = ({
                         src,
                         date,
                         title,
                         url
                     }) => {
    return (
        <div className="landing-metinvest-articles-card-frame">
            <a
                target={"_blank"}
                href={url}>
                <div>
                    <img
                        src={src}
                        className="landing-metinvest-article-card-image"
                        alt="Картинка Статті Metinvest"
                    />
                </div>

                <div className="landing-metinvest-article-meta-frame">
                    <div className="row">
                        <div className="col-12 text-left">
                        <span className="landing-metinvest-article-card-date font-weight-400">
                            {date}
                        </span>
                        </div>
                    </div>

                    <div className="row margin-top-10">
                        <div className="col-12 text-left">
                        <span className="landing-metinvest-article-card-title font-weight-500">
                            {title}
                        </span>
                        </div>
                    </div>
                </div>

                <div className="row landing-metinvest-articles-arrow-div">
                    <div className="col-12 text-right">
                        <img
                            className="landing-metinvest-articles-arrow"
                            src={srcArrow}
                            alt="Стаття Metinvest"
                        />
                    </div>
                </div>
            </a>
        </div>
    );
};

export {
    CardArticle as default
};
