import React from "react";

import { useMediaQuery } from "react-responsive";

import Winner from "../../../../configs/application/strings/landing/metinvest/Winners";

import CardWinner from "../../genesis/cards/CardWinner";


const BlockWinners = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    if (isTabletOrMobileDevice)
        return (
            <div className="container-fluid">
                <div className="row">
                    <div className="col-12">
                        <p className="landing-metinvest-header-mobile font-weight-500">Переможці</p>
                    </div>
                </div>

                <div className="row">
                    <div className="col-12">
                        <p className="landing-metinvest-subheader font-weight-400">Перевіряй усю інформацію про призи на своєму e-mail</p>
                    </div>
                </div>

                <div className="row row-center">
                    <CardWinner
                        title={"ТОП 20 ⭐️"}
                        description={Winner.extract("top-20")}
                    />
                </div>
            </div>
        );

    return (
        <div className="container-fluid margin-top-40">
            <div className="row">
                <div className="col-12">
                    <p className="landing-metinvest-header font-weight-600">Переможці</p>
                </div>
            </div>

            <div className="row">
                <div className="col-12">
                    <p className="landing-metinvest-subheader font-weight-400">Перевіряй усю інформацію про призи на своєму e-mail</p>
                </div>
            </div>

            <div className="row row-center margin-top-20">
                <CardWinner
                    title={"ТОП 20 ⭐️"}
                    description={Winner.extract("top-20")}
                />
            </div>
        </div>
    );
};

export {
    BlockWinners as default
};
