import React from 'react';

import srcMenu from "../../../images/icons/modified/menu.svg";

import {
    withRouter
} from "react-router-dom";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
    DirectLink
} from 'react-scroll';

import * as ReactGA from "react-ga";


const NavBarBunchBusinessFinance = ({
                                      isTranslationOn
                                  }) => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    return (
        <nav
            role="navigation"
            className="navbar navbar-expand-lg">
            <a
                href="/"
                className="navbar-brand font-weight-500"
                onClick={evt => {
                    ReactGA.event({
                        category: 'Landing Metinvest',
                        action: 'Click Big Career Fest. Online',
                        label: 'NavBar'
                    });
                }}>
                BIG CAREER FEST. online
            </a>

            <button
                className="navbar-toggler"
                type="button"
                data-toggle="collapse"
                data-target="#navbarText"
                aria-controls="navbarText"
                aria-expanded="false"
                aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon">
                        <img
                            src={srcMenu}
                            className="menu-toggler"/>
                    </span>
            </button>

            <div
                className="collapse navbar-collapse"
                id="navbarText">
                <ul className="navbar-nav ml-auto"/>
                <ul className="navbar-nav ml-auto">
                    {
                        isTranslationOn
                            ? (
                                <li className="nav-item mr-2">
                                    <Link
                                        onClick={evt => {
                                            ReactGA.event({
                                                category: 'Landing Metinvest',
                                                action: 'Click About Company',
                                                label: 'NavBar'
                                            });
                                        }}
                                        activeClass="active"
                                        className="test2"
                                        to="business-finance-stream"
                                        spy={true}
                                        smooth={true}
                                        duration={1500}
                                    >
                                        <li
                                            className={
                                                isTabletOrMobileDevice
                                                    ? "nav-item nav-item-text font-weight-400"
                                                    : "nav-item nav-item-text font-weight-500"
                                            }>
                                            Дивитись стрім
                                        </li>
                                    </Link>
                                </li>
                            ) : null
                    }

                    <li className="nav-item mr-2">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="business-finance-program"
                            spy={true}
                            smooth={true}
                            duration={1500}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Bunch Marketing',
                                        action: 'Click Game',
                                        label: 'NavBar'
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Розклад
                            </a>
                        </Link>
                    </li>

                    <li className="nav-item mr-2">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="business-finance-companies"
                            spy={true}
                            smooth={true}
                            duration={1500}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest / NavBar',
                                        action: 'Click Vacancies',
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Компанії банчу
                            </a>
                        </Link>
                    </li>

                    <li className="nav-item mr-2">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="business-finance-testing"
                            spy={true}
                            smooth={true}
                            duration={1500}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest / NavBar',
                                        action: 'Click Blog',
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Отримати сертифікат
                            </a>
                        </Link>
                    </li>
                </ul>
            </div>
        </nav>
    );
};

export default withRouter(NavBarBunchBusinessFinance);
