import React from 'react';

import {
    useMediaQuery
} from "react-responsive";

import srcEmail from "../../../../images/landings/metinvest/networks/email.png";
import srcFacebook from "../../../../images/landings/metinvest/networks/facebook.svg";
import srcInstagram from "../../../../images/landings/metinvest/networks/instagram.svg";
import srcTelegram from "../../../../images/landings/metinvest/networks/telegram.svg";

import { Link } from "react-router-dom";

import * as ReactGA from "react-ga";


const BlockSocialNetworks = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    if (isTabletOrMobileDevice)
        return (
            <div className="container-fluid landing-metinvest-networks-background padding-tb-40">
                <div className="col-12">
                    <div className="row">
                        <div className="col-12 text-center">
                            <span className="landing-metinvest-header-mobile font-weight-500">Соцмережі METINVEST</span>
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12 text-center">
                            <Link
                                className="mr-4"
                                target="_blank"
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest',
                                        action: 'Click Facebook',
                                        label: 'Block Social Networks',
                                    });
                                }}
                                to={{
                                    pathname: "https://www.facebook.com/MetinvestZ"
                                }}>
                                <img
                                    src={srcFacebook}
                                    alt={"Metinvest Фейсбук"}
                                />
                            </Link>

                            <Link
                                className="ml-4 mr-4"
                                target="_blank"
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest',
                                        action: 'Click Instagram',
                                        label: 'Block Social Networks',
                                    });
                                }}
                                to={{
                                    pathname: "https://www.instagram.com/metinvestz"
                                }}>
                                <img
                                    src={srcInstagram}
                                    alt={"Metinvest Інстаграм"}
                                />
                            </Link>

                            <Link
                                className="ml-4"
                                target="_blank"
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest',
                                        action: 'Click Telegram',
                                        label: 'Block Social Networks',
                                    });
                                }}
                                to={{
                                    pathname: "http://t.me/metinvest"
                                }}>
                                <img
                                    src={srcTelegram}
                                    alt={"Metinvest Телеграм"}
                                />
                            </Link>
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12 text-center">
                        <span className="landing-metinvest-networks-email-text font-weight-500">
                            А також надсилай резюме на пошту
                        </span>
                        </div>
                    </div>

                    <div className="row margin-top-10">
                        <div className="col-12 text-center">
                            <Link
                                target="_blank"
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest',
                                        action: 'Click Email',
                                        label: 'Block Social Networks',
                                    });
                                }}
                                to={{
                                    pathname: "mailto:resume@metinvestholding.com"
                                }}>
                                <img
                                    src={srcEmail}
                                    height={"55px"}
                                    width={"55px"}
                                    alt={"Metinvest Email"}
                                />
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        );

    return (
        <div className="container-fluid landing-metinvest-networks-background padding-tb-60">
            <div className="col-12">
                <div className="row">
                    <div className="col-3"/>
                    <div className="col-6 text-center">
                        <span className="landing-metinvest-networks-title font-weight-600">Соцмережі METINVEST</span>
                    </div>
                    <div className="col-3"/>
                </div>

                <div className="row margin-top-40">
                    <div className="col-3"/>
                    <div className="col-6 text-center">
                        <Link
                            className="mr-4"
                            target="_blank"
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing Metinvest',
                                    action: 'Click Facebook',
                                    label: 'Block Social Networks',
                                });
                            }}
                            to={{
                                pathname: "https://www.facebook.com/MetinvestZ"
                            }}>
                            <img
                                src={srcFacebook}
                                alt={"Metinvest Фейсбук"}
                            />
                        </Link>

                        <Link
                            className="ml-4 mr-4"
                            target="_blank"
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing Metinvest',
                                    action: 'Click Instagram',
                                    label: 'Block Social Networks',
                                });
                            }}
                            to={{
                                pathname: "https://www.instagram.com/metinvestz"
                            }}>
                            <img
                                src={srcInstagram}
                                alt={"Metinvest Інстаграм"}
                            />
                        </Link>

                        <Link
                            className="ml-4"
                            target="_blank"
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing Metinvest',
                                    action: 'Click Telegram',
                                    label: 'Block Social Networks',
                                });
                            }}
                            to={{
                                pathname: "http://t.me/metinvest"
                            }}>
                            <img
                                src={srcTelegram}
                                alt={"Metinvest Телеграм"}
                            />
                        </Link>
                    </div>
                    <div className="col-3"/>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12 text-center">
                        <span className="landing-metinvest-networks-email-text font-weight-500">
                            А також надсилай резюме на пошту
                        </span>
                    </div>
                </div>

                <div className="row margin-top-10">
                    <div className="col-12 text-center">
                        <Link
                            target="_blank"
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing Metinvest',
                                    action: 'Click Email',
                                    label: 'Block Social Networks',
                                });
                            }}
                            to={{
                                pathname: "mailto:resume@metinvestholding.com"
                            }}>
                            <img
                                src={srcEmail}
                                height={"55px"}
                                width={"55px"}
                                alt={"Metinvest Email"}
                            />
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export {
    BlockSocialNetworks as default
};
