import React from "react";

import srcBriefcase from "../../../../../images/icons/modified/briefcase.svg";

import { useMediaQuery } from "react-responsive";

import CardMetinvest, { CardMetinvestMobile } from "./card/CardMetinvest";
import * as ReactGA from "react-ga";


const BlockMetinvest = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    if (isTabletOrMobileDevice)
        return (
            <div className="container-fluid padding-tb-40">
                <div className="row">
                    <div className="col-12 text-center">
                    <span
                        className="landing-metinvest-header-mobile font-weight-600">
                        <a
                            className="landing-metinvest-header-not-a"
                            target={"_blank"}
                            href={"/metinvest"}>
                            Metinvest
                        </a>
                    </span>
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12 text-center">
                    <span
                        className="landing-metinvest-subheader font-weight-400">
                        Гідна робота в рідній країні.
                    </span>
                        <br/>
                        <span
                            className="landing-metinvest-subheader font-weight-400">
У компанії працює понад 80 тисяч співробітників на підприємствах в Україні, Італії, Великій Британії, Болгарії та США.
                        </span>
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12">
                        <CardMetinvestMobile
                            src={srcBriefcase}
                            title={"Заповнюй форму та працевлаштовуйся"}
                            description={"Не забудь додати своє резюме, щоб показати себе з якомога кращої сторони"}
                            link={"/metinvest/#metinvest-registration"}
                            buttonText={"Перейти до форми"}
                            cssColorBackground={"landing-metinvest-card-employment"}
                            cssColorButton={"landing-metinvest-card-button-employment"}
                        />
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12">
                        <CardMetinvestMobile
                            src={srcBriefcase}
                            title={"Вакансії та стажування для студентів"}
                            description={"Переходь за лінком, дізнайся про найкращі можливості, стажування та вакансії у Metinvest"}
                            link={"/metinvest/#metinvest-vacancies"}
                            buttonText={"До можливостей"}
                            cssColorBackground={"landing-metinvest-card-opportunities"}
                            cssColorButton={"landing-metinvest-card-button-opportunities"}
                        />
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12">
                        <CardMetinvestMobile
                            src={srcBriefcase}
                            title={"Інтерактивна гра “Залізна Логіка”"}
                            description={"Реєструйся, вивчай, грай - та вигравай найкрутіші брендовані набори Metinvest"}
                            link={"/metinvest/#metinvest-game"}
                            buttonText={"Грати"}
                            cssColorBackground={"landing-metinvest-card-game"}
                            cssColorButton={"landing-metinvest-card-button-game"}
                        />
                    </div>
                </div>

                <div className="row margin-top-40">
                    <div className="col-12 text-center">
                        <a
                            target={"_blank"}
                            href={"/metinvest"}>
                            {/*button-registrate margin-genesis*/}
                            <button
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click About Company',
                                        label: 'Block Metinvest',
                                    });
                                }}
                                className="landing-metinvest-button-about-company font-weight-500">
                                Більше про компанію
                            </button>
                        </a>
                    </div>
                </div>
            </div>
        );

    return (
        <div className="container-fluid padding-tb-60">
            <div className="row">
                <div className="col-12 text-center">
                    <span
                        className="landing-metinvest-header font-weight-600">
                        <a
                            className="landing-metinvest-header-not-a"
                            target={"_blank"}
                            href={"/metinvest"}>
                            Metinvest
                        </a>
                    </span>
                </div>
            </div>

            <div className="row margin-top-20">
                <div className="col-12 text-center">
                    <span
                        className="landing-metinvest-subheader font-weight-400">
                        Гідна робота в рідній країні.
                    </span>
                    <br/>
                    <span
                        className="landing-metinvest-subheader font-weight-400">
У компанії працює понад 80 тисяч співробітників на підприємствах в Україні, Італії, Великій Британії, Болгарії та США.
                    </span>
                </div>
            </div>

            <div className="row margin-top-40">
                <div className="col-4">
                    <CardMetinvest
                        src={srcBriefcase}
                        title={"Заповнюй форму та працевлаштовуйся"}
                        description={"Не забудь додати своє резюме, щоб показати себе з якомога кращої сторони"}
                        link={"/metinvest/#metinvest-registration"}
                        buttonText={"Перейти до форми"}
                        cssColorBackground={"landing-metinvest-card-employment"}
                        cssColorButton={"landing-metinvest-card-button-employment"}
                    />
                </div>
                <div className="col-4">
                    <CardMetinvest
                        src={srcBriefcase}
                        title={"Вакансії та стажування для студентів"}
                        description={"Переходь за лінком, дізнайся про найкращі можливості, стажування та вакансії у Metinvest"}
                        link={"/metinvest/#metinvest-vacancies"}
                        buttonText={"До можливостей"}
                        cssColorBackground={"landing-metinvest-card-opportunities"}
                        cssColorButton={"landing-metinvest-card-button-opportunities"}
                    />
                </div>
                <div className="col-4">
                    <CardMetinvest
                        src={srcBriefcase}
                        title={"Інтерактивна гра “Залізна Логіка”"}
                        description={"Реєструйся, вивчай, грай - та вигравай найкрутіші брендовані набори Metinvest"}
                        link={"/metinvest/#metinvest-game"}
                        buttonText={"Грати"}
                        cssColorBackground={"landing-metinvest-card-game"}
                        cssColorButton={"landing-metinvest-card-button-game"}
                    />
                </div>
            </div>

            <div className="row margin-top-40">
                <div className="col-12 text-center">
                    <a
                        target={"_blank"}
                        href={"/metinvest"}>
                        {/*button-registrate margin-genesis*/}
                        <button
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF',
                                    action: 'Click About Company',
