import React from 'react';

import * as MUIRadio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import FormControl from '@material-ui/core/FormControl';


const SelectRadio = (props) => {
    return (
        <FormControl
            id="course-select-course-form-2-mobile"
            className="no-label-margin">
            <RadioGroup
                aria-label="position"
                id="mui-worktype"
                name="position"
                value={props.value}
                onChange={props.onChange}
            >
                {props.content.map(option => (
                    <FormControlLabel
                        value={option.name}
                        id="radio-box-black"
                        control={<MUIRadio color={'primary'} />}
                        label={option.name}
                        labelPlacement="end"
                    />
                ))}
            </RadioGroup>
        </FormControl>
    );
};

const Radio = (props) => {
    const {
        question,
        required,
        description,
        activeDescriptionText
    } = props;

    return (
        <div>
            <div className="input-title-apply">
                {question}
                {
                    required
                        ? <div className="required-mark">*</div>
                        : null
                }
            </div>
            {
                activeDescriptionText
                    ? <p className="short-description-student">
                        {description}
                      </p>
                    : null
            }
            <div className="row">
                <div className="col-12 margin-bottom--10">
                    <SelectRadio
                        {...props}
                    />
                </div>
            </div>
        </div>
    );
};


export {
    SelectRadio,
    Radio as default,
};
