import React from 'react';


class CardActivity extends React.Component {
    render() {
        return (
            <div className="col-12 col-md-6 col-lg-3 padding-top-bottom-activity padding-top-bottom-mobile">
                <div className="row row-icon-block">
                    <div className="col-12 row-center">
                        <img
                            src={this.props.image}
                            className="icon-block"
                            alt={"company-star"}/>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <p className="title-small-block font-weight-500">{this.props.title}</p>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <p className="title-small-block-text font-weight-300">{this.props.explanation}</p>
                    </div>
                </div>
            </div>
        );
    }
}

export {
    CardActivity as default
};
