import React, { useState } from "react";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
    Redirect,
} from "react-router-dom";

import {
    Element
} from "react-scroll/modules";

import {
    Animated
} from "react-animated-css";

import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from "@material-ui/core/Snackbar/Snackbar";

import {
    BlockResume,
    BlockSocialNetworks
} from "./BlockEntities";

// Import Google Firebase modules
import {
    storage,
    ResumesBCF
} from '../../../../../configs/google/firebase/config';

import * as ReactGA from "react-ga";


const Alert = (props) => {
    return <MuiAlert
        elevation={6}
        variant="filled"
        {...props} />;
};


const BlockRegistrationEvent = (props) => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    const [state, setState]
        = useState({
        application: {
            file: {
                name: null,
                data: null
            },
        },
        // Boolean triggers
        isError: false,
        isConfirm: true,
        isUploaded: false,
        // Error state controllers
        redirect: null,
        errorMsg: null,
        errorStatus: null,
        errorFields: []
    });

    const getInitialState = () => {
        return {
            isError: false,
            redirect: null,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onSelectFile = () => {
        const selector = document.createElement('input');
        selector.setAttribute('type', 'file');
        selector.setAttribute('multiple', 'multiple');
        selector.onchange
            = evt => {
            setState({
                ...state,
                application: {
                    ...state.application,
                    file: evt.target.files[0]
                },
                isUploaded: true
            });
        };
        return selector;
    };

    const fileSelector = onSelectFile();

    const onClickFileUpload = (evt) => {
        evt.preventDefault();
        fileSelector.click();
        // Trigger click resume event
        ReactGA.event({
            category: 'Landing BCF',
            action: 'Click Resume',
            label: 'Block Registration'
        });
    };

    const onClickFileRemove = (evt) => {
        setState({
            ...state,
            isUploaded: false,
            application: {
                ...state.application,
                file: {
                    name: null,
                    data: null
                }
            }
        });
    };

    const onClickConfirmation = (evt) => {
        setState({
            ...state,
            isConfirm: !state.isConfirm
        });
    };

    const onClickRegistration = (evt) => {
        if (!state.isUploaded) {
            setState({
                ...state,
                isError: true,
                errorMsg: "Залий резюме, перед тим як працевлаштуватись)",
                errorStatus: 403
            });
            return;
        }

        /* Show success window */
        setState({
            ...state,
            isSuccess: true
        });

        /* If application has file, upload it in Firebase */
        if (state.isUploaded) {
            const
                timeString = new Date().toISOString(),
                fileName = state.application.file.name,
                fileNameFirebase = `${timeString}-${fileName}`,
                uploadTask = storage
                    .ref(ResumesBCF)
                    .child(fileNameFirebase)
                    .put(state.application.file);
            /* Upload image in the cloud */
            uploadTask.on(
                'state_changed',
                async snapshot => {},
                err => console.log(err),
                complete => {
                    storage
                        .ref(ResumesBCF)
                        .child(fileNameFirebase)
                        .getDownloadURL()
                        .then(url => {});
                },
            );
        }
    };

    const onCloseAlert = (evt) => {
        setState({
            ...state,
            ...getInitialState()
        });
    };

    if (state.redirect)
        return (
            <Redirect
                external
                target={"_blank"}
                to={"https://rebrand.ly/nrlflkb"}
            />
        );

    return (
        <Element
            id="registration"
            name="registration"
            className="element">
            <div
                className={
                    isTabletOrMobileDevice
                        ? "container-fluid block-registration padding-bottom-30 margin-top-30"
                        : "container-fluid block-registration padding-bottom-30"
                }>
                <div className="row">
                    <div className="container">
                        <div
                            className={
                                isTabletOrMobileDevice
                                    ? "row margin-top-60"
                                    : "row margin-top-60"
                            }>
                            <div className="col-12">
                                <p
                                    className={
                                        isTabletOrMobileDevice
                                            ? "landing-registration-title-mobile font-weight-600"
                                            : "landing-registration-title font-weight-600"
                                    }>
                                    Отримуй роботу
                                </p>
                            </div>
                        </div>

                        <div className="row row-center">
                            <div className="col-12 col-lg-10">
                                <p className="title-block-expl font-weight-400">
                                    Обирай резюме - клікай працевлаштуватись, все просто!
                                </p>
                            </div>
                        </div>

                        <Snackbar
                            open={state.isError}
                            autoHideDuration={6000}
                            onClose={null}>
                            <Alert
                                onClose={onCloseAlert}
                                severity="error">
                                {state.errorMsg}
                            </Alert>
                        </Snackbar>

                        <Element
                            id="bcfRegistration"
                            name="bcfRegistration"
                            className="element">
                            {(() => {
                                if (!state.isSuccess)
                                    return (
                                        <div className="row row-center padding-top-organizators">
                                            <div className="col-12 col-md-10 col-lg-8">
                                                <div className="col-12 margin-bottom-20 mt-3">
                                                    <div className="row">
                                                        <div className="col-12">
                                                            <div className="row">
                                                                <div className="col-12 col-lg-12">
                                                                    <p className="label-form-genesis font-weight-400">
                                                                        <span className="font-weight-500">
                                                                            Резюме
                                                                        </span>
                                                                        <div className="required-mark">
                                                                            <b>*</b>
                                                                        </div>
                                                                        {" "}(це можливість потрапити у базу компаній та отримати роботу, поле необов’язкове)
                                                                    </p>
                                                                </div>
                                                            </div>

                                                            <div className="row">
                                                                <div className="col-12 col-12-lg">
                                                                    <BlockResume
                                                                        isUploaded={state.isUploaded}
                                                                        fileName={state.application.file.name}
                                                                        onClickFileRemove={onClickFileRemove}
                                                                        onClickFileUpload={onClickFileUpload}
                                                                        isTabletOrMobileDevice={isTabletOrMobileDevice}
                                                                    />
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>

                                                    <div className="row margin-top-20">
                                                        <div className="col-12 col-lg-12">
                                                            <div className="form-group form-check">
                                                                <input
                                                                    id="checkboxConfirmation"
                                                                    name={"isConfirm"}
                                                                    type="checkbox"
                                                                    className="form-check-input active"
                                                                    checked={state.isConfirm}
                                                                    onChange={onClickConfirmation}
                                                                />
                                                                <label className="form-check-label" htmlFor="checkboxConfirmation">
                                                                    Надаю згоду на {' '}
                                                                    <a
                                                                        target={"_blank"}
                                                                        href="https://bit.ly/2Yu3Vdl">
                                                                        обробку персональних даних
                                                                    </a>
                                                                </label>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="col-12 mt-3">
                                                    <button
                                                        type="submit"
                                                        onClick={
                                                            state.isConfirm
                                                                ? onClickRegistration
                                                                : null
                                                        }
                                                        className={
                                                            // state.isConfirm && state.isUploaded
                                                            state.isConfirm
                                                                ? "landing-registration-button-registration font-weight-500"
                                                                : "landing-registration-button-registration font-weight-500 disabled"
                                                        }>
                                                        Працевлаштуватись
                                                    </button>
                                                </div>

                                                <BlockSocialNetworks
                                                    isTabletOrMobileDevice={isTabletOrMobileDevice}
                                                />
                                            </div>
                                        </div>
                                    );

                                return (
                                    <div className="row row-center padding-top-organizators">
                                        <div className="col-12 col-md-10 col-lg-8">
                                            <div className="row">
                                                <div className="col-12 col-lg-12 row-center">
                                                    <Animated
                                                        animationIn="fadeIn"
                                                        animationOut="fadeOut"
                                                        animationInDuration={400}
                                                        animationOutDuration={400}
                                                        isVisible={true}>
                                                        <p className="thank-you">
                                                            Дякуємо, чекай на новини від компаній :3
                                                        </p>
                                                    </Animated>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                );
                            })()}
                        </Element>
                    </div>
                </div>
            </div>
        </Element>
    );
};


export {
    BlockRegistrationEvent as default
};
