import React from "react";

import {
    useMediaQuery
} from "react-responsive";

import * as ReactGA from "react-ga";

import CardGenesisGift from "../../genesis/cards/CardGift";


const BlockOlympiad = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    return (
        <div
            className={
                isTabletOrMobileDevice
                    ? "container background-white padding-top-bottom-30"
                    : "container background-white padding-tb-60"
            }>
            <div className="row">
                <div className="col-12">
                    <p
                        className={
                            isTabletOrMobileDevice
                                ? "landing-olympiad-title-mobile font-weight-600"
                                : "landing-olympiad-title font-weight-600"
                        }>
                        <a
                            className="landing-metinvest-header-not-a"
                            target={"_blank"}
                            href={"/genesis-olympiad"}>
                            Олімпіада від Genesis
                        </a>
                    </p>
                </div>
            </div>

            <div className="row row-center">
                <div className="col-12 col-lg-10">
                    <p className="title-block-expl font-weight-400">
                        Випробовуй скіли у вирішенні математичних тестів від однієї з найбільших продуктових компаній України!
                    </p>
                </div>
            </div>

            <div className="row row-center">
                <CardGenesisGift
                    title={"ТОП 1"}
                    description={"Кар'єрна консультація у вигляді Online Coffee від co-founder Genesis"}
                    isLast={false}
                />
                <CardGenesisGift
                    title={"ТОП 5"}
                    description={"Круті набори з брендованою продукцією від Genesis"}
                    isLast={false}
                />
                <CardGenesisGift
                    title={"ТОП 20"}
                    description={"Сертифікат про успішну участь у олімпіаді, які спрощують проходження на Genesis IT School 10.0"}
                    isLast={false}
                />
            </div>

            <div className="row">
                <div className="col-12 row-center">
                    <a href={"/genesis-olympiad"}>
                        {/*button-registrate margin-genesis*/}
                        <button
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF',
                                    action: 'Click Registration',
                                    label: 'Block Olympiad',
                                });
                            }}
                            className="landing-olympiad-button-registration font-weight-500">
                            Більше про олімпіаду
                        </button>
                    </a>
                </div>
            </div>
        </div>
    );
};

export {
    BlockOlympiad as default
};
