import React from 'react';

import BlockGreetings, {
    BlockGreetingsRegistration
} from './blocks/BlockGreetings';

import Footer from "../../footer/Footer";

// import BlockLectures from "./blocks/lectures/BlockLectures";
import BlockBunches from "./blocks/bunches/BlockBunches";
import BlockMetinvest from "./blocks/metinvest/BlockMetinvest";
import BlockOlympiad from "./blocks/BlockOlympiad";
import BlockCompanies from "./blocks/companies/BlockCompanies";
import BlockActivities from "./blocks/activities/BlockActivities";
import BlockOrganizators from "./blocks/organizators/BlockOrganizators";
import BlockInfoPartners from "./blocks/info-partners/BlockInfoPartners";
import BlockRegistration from "./blocks/registration/BlockRegistration";
import BlockRegistrationEvent from "./blocks/registration/BlockRegistrationEvent";


export default class LandingBCF extends React.Component {
    constructor(props) {
        super(props);
        this.scrollCompanies = React.createRef();
        this.scrollRegistrate = React.createRef();
        this.scrollOrganizators = React.createRef();
    }

    componentDidMount() {
        const hash = this.props.location.hash.substr(1);

        switch (hash) {
            case "companies":
                window.scrollTo({
                    behavior: 'smooth',
                    top: this.scrollCompanies.current.offsetTop
                });
                break;

            case "organizators":
                window.scrollTo({
                    behavior: 'smooth',
                    top: this.scrollOrganizators.current.offsetTop
                });
                break;

            case "registration":
                window.scrollTo({
                    behavior: 'smooth',
                    top: this.scrollRegistrate.current.offsetTop
                });
                break;
        }
    }

    render() {
        return (
            <div>
                <BlockGreetingsRegistration/>
                <BlockBunches/>
                <BlockActivities/>
                <BlockMetinvest/>
                <BlockOlympiad/>
                <BlockCompanies/>
                <BlockInfoPartners/>
                <BlockOrganizators/>
                {/*<BlockRegistration/>*/}
                <BlockRegistrationEvent/>
                <Footer/>
            </div>
        );
    }
}
