import React, { useEffect, useState } from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import { Element } from 'react-scroll'

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
    Redirect,
} from "react-router-dom";

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
        input: {
            display: 'none',
        },
    },
}));

export default function Registration() {
    const classes = useStyles();

    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    const [state, setState] = useState({
        // application: {
        //     email: null,
        //     // file: {
        //     //     name: null,
        //     //     data: null
        //     // },
        // },
        // Boolean triggers
        // isError: false,
        isConfirm: true,
        // isUploaded: false,
        // Error state controllers
        redirect: null,
        errorMsg: null,
        errorStatus: null,
        errorFields: []
    });

    const getInitialState = () => {
        return {
            isError: false,
            redirect: null,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onClickConfirmation = (evt) => {
        setState({
            ...state,
            isConfirm: !state.isConfirm
        });
    };

    // if (state.redirect)
    //     return (
    //         <Redirect
    //             external
    //             target={"_blank"}
    //             to={"https://rebrand.ly/nrlflkb"}
    //         />
    //     );

    return (
        <Element name="registration" className="element" >
            <div className="container padding-bottom-blocks container-registration">
                <div className="row center-row">
                    <div className="col-8 center-row">
                        <p className="title-block-text">Реєстрація</p>
                    </div>
                </div>
                <div className="row center-row">
                    <div className="col-6 center-row">
                        <p className="title-block-secondary-text">
                            Реєструйся, щоб взяти участь в заході та отримати доступ до навчання
                        </p>
                    </div>
                </div>
                <div className="row center-row">
                    <div className="col-6 center-row">
                        <form className={classes.root} noValidate autoComplete="off">
                            <div>
                                <label className="label-registration-text">Ім'я*</label>
                                <input placeholder="Петро" className="input-box-registration"/>
                            </div>
                            <div>
                                <label className="label-registration-text">Email*</label>
                                <input type="email" placeholder="email@email.com" className="input-box-registration"/>
                            </div>
                            <div>
                                <label className="label-registration-text">Курс*</label>
                                <input type="number" placeholder="3" className="input-box-registration"/>
                            </div>
                            <div>
                                <label className="label-registration-text">Сфера*</label>
                                <input type="select" className="input-box-registration"/>
                            </div>
                            <div className="resume-label-div">
                                <label className="label-resume-registration-text">Резюме&nbsp;</label>
                                <span className="opportunity-registartion">(можливість потрапити в базу компаній та отримати роботу, поле необов'язкове)</span>
                            </div>
                            <div>
                                <input
                                    accept="image/*"
                                    className="input-upload-file"
                                    id="contained-button-file"
                                    multiple
                                    type="file"
                                />
                                <label htmlFor="contained-button-file">
                                    <button data-toggle="contained-button-file" className="landing-registration-button-resume">
                                        <i className="fi-rr-cloud-upload registration"></i>
                                        Завантажити резюме
                                    </button>
                                </label>
                            </div>
                            <div className="row margin-top-20">
                                <div className="col-12 col-lg-12">
                                    <div className="form-group form-check">
                                        <input
                                            id="checkboxConfirmation"
                                            name={"isConfirm"}
                                            type="checkbox"
                                            className="form-check-input active"
                                            checked={state.isConfirm}
                                            onChange={onClickConfirmation}
                                        />
                                        <label className="form-check-label" htmlFor="checkboxConfirmation">
                                            Надаю згоду на {' '}
                                            <a
                                                className="link-data-registration"
                                                target={"_blank"}
                                                href="https://bit.ly/2Yu3Vdl">
                                                обробку персональних даних
                                            </a>
                                        </label>
                                    </div>
                                </div>
                            </div>
                            <div className="button-final-center">
                                <button className="registrate-final-button">Зареєструватись</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </Element>
    );
}
