import React from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import { Element } from 'react-scroll'

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
        input: {
            display: 'none',
        },
    },
}));



export default function Registration() {
    const classes = useStyles();



    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    const [state, setState]
        = useState({
        application: {
            email: null,
            file: {
                name: null,
                data: null
            },
        },
        // Boolean triggers
        isError: false,
        isConfirm: true,
        isUploaded: false,
        // Error state controllers
        redirect: null,
        errorMsg: null,
        errorStatus: null,
        errorFields: []
    });

    const getInitialState = () => {
        return {
            isError: false,
            redirect: null,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onSelectFile = () => {
        const selector = document.createElement('input');
        selector.setAttribute('type', 'file');
        selector.setAttribute('multiple', 'multiple');
        selector.onchange
            = evt => {
            setState({
                ...state,
                application: {
                    ...state.application,
                    file: evt.target.files[0]
                },
                isUploaded: true
            });
        };
        return selector;
    };

    const fileSelector = onSelectFile();

    const asyncRegistrationRequest = async () => {
        try {
            const response = await axios.post(POST_REGISTRATION_REQUEST, {
                ...state.application
            });
            return response.data;
        } catch (err) {
            switch (err.response.status) {
                case 403:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: err.response.data.data.errors[0].msg,
                        errorStatus: err.response.status,
                        errorFields: err.response.data.data.errors
                            .map(errorObj => errorObj.param),
                    });
                    break;

                case 409:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: "Ви вже реєстувались :3",
                        errorStatus: err.response.status,
                        errorFields: ["email"]
                    });
                    break;

                case 500:
                    setState({
                        ...state,
                        isError: true,
                        errorStatus: err.response.status,
                        redirect: '/500'
                    });
                    break;
            }
            throw err;
        }
    };

    const onChangeApplication
        = (evt) => {
        setState({
            ...state,
            application: {
                ...state.application,
                [evt.target.name]: evt.target.value
            }
        });
    };

    const onClickFileUpload = (evt) => {
        evt.preventDefault();
        fileSelector.click();
        // Trigger click resume event
        ReactGA.event({
            category: 'Landing BCF',
            action: 'Click Resume',
            label: 'Block Registration'
        });
    };

    const onClickFileRemove = (evt) => {
        setState({
            ...state,
            isUploaded: false,
            application: {
                ...state.application,
                file: {
                    name: null,
                    data: null
                }
            }
        });
    };

    const onClickConfirmation = (evt) => {
        setState({
            ...state,
            isConfirm: !state.isConfirm
        });
    };

    const onClickRegistration = (evt) => {
        (async () => {
            const data = await asyncRegistrationRequest();
            if (data.isSuccess) {
                setState({
                    ...state,
                    ...data,
                    ...getInitialState(),
                });
                // Trigger event registration
                ReactGA.event({
                    category: 'Landing BCF',
                    action: 'Submit Registration',
                    label: 'Block Registration',
                });
                /* If user not uploaded file - end function */
                if (!state.isUploaded) {
                    // Redirect to the Telegram Channel
                    await delay(1000);
                    window.location.replace("https://rebrand.ly/nrlflkb");
                    return;
                }
                /* If application has file, upload it in Firebase */
                if (state.isUploaded) {
                    const
                        timeString = new Date().toISOString(),
                        fileName = state.application.file.name,
                        fileNameFirebase = `${state.application.email}-${timeString}-${fileName}`,
                        uploadTask = storage
                            .ref(ResumesBCF)
                            .child(fileNameFirebase)
                            .put(state.application.file);
                    /* Upload image in the cloud */
                    uploadTask.on(
                        'state_changed',
                        async snapshot => {
                        },
                        err => console.log(err),
                        complete => {
                            storage
                                .ref(ResumesBCF)
                                .child(fileNameFirebase)
                                .getDownloadURL()
                                .then(url => {
                                    // Redirect to the Telegram Channel
                                    window.location.replace("https://rebrand.ly/nrlflkb");
                                });
                        },
                    );
                }
            }
        })();
    };

    const onCloseAlert = (evt) => {
        setState({
            ...state,
            ...getInitialState()
        });
    };

    if (state.redirect)
        return (
            <Redirect
                external
                target={"_blank"}
                to={"https://rebrand.ly/nrlflkb"}
            />
        );


    return (
        <Element name="registration" className="element" >
            <div className="container padding-bottom-blocks">
                <div className="row center-row">
                    <div className="col-8 center-row">
                        <p className="title-block-text">Реєстрація</p>
                    </div>
                </div>
                <div className="row center-row">
                    <div className="col-6 center-row">
                        <p className="title-block-secondary-text">
                            Реєструйся, щоб взяти участь в заході та отримати доступ до навчання
                        </p>
                    </div>
                </div>
                <div className="row center-row">
                    <div className="col-10 center-row">
                        <form className={classes.root} noValidate autoComplete="off">
                            <div>
                                <TextField required id="standard-required" label="Ім`я" />
                            </div>
                            <div>
                                <TextField required type="email" id="standard-required" label="Email" />
                            </div>
                            <div>
                                <TextField required type="number" id="standard-required" label="Курс" />
                            </div>
                            <div>
                                <TextField required id="standard-required" label="Сфера" />
                            </div>
                            <div>
                                <label className="label-registration-text">Резюме&nbsp;</label>
                                <label>(можливість потрапити в базу компаній та отримати роботу, поле необов'язкове)</label>
                            </div>
                            <div>
                                <input
                                    accept="image/*"
                                    className="input-upload-file"
                                    id="contained-button-file"
                                    multiple
                                    type="file"
                                />
                                <label htmlFor="contained-button-file">
                                    <Button variant="contained" color="primary" component="span">
                                        Завантажити резюме
                                    </Button>
                                </label>
                            </div>
                            <div className="row margin-top-20">
                                <div className="col-12 col-lg-12">
                                    <div className="form-group form-check">
                                        <input
                                            id="checkboxConfirmation"
                                            name={"isConfirm"}
                                            type="checkbox"
                                            className="form-check-input active"
                                            checked={state.isConfirm}
                                            onChange={onClickConfirmation}
                                        />
                                        <label className="form-check-label" htmlFor="checkboxConfirmation">
                                            Надаю згоду на {' '}
                                            <a
                                                target={"_blank"}
                                                href="https://bit.ly/2Yu3Vdl">
                                                обробку персональних даних
                                            </a>
                                        </label>
                                    </div>
                                </div>
                            </div>
                            <div>
                                <button className="registrate-final-button">Зареєструватись</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </Element>
    );
}
