import React from 'react';

import srcArrowLeft from "../../../../images/bunch/marketing/curve-arrow.svg";
import srcArrowDown from "../../../../images/bunch/marketing/arrow-down.png";

import { useMediaQuery } from "react-responsive";

import { Element } from "react-scroll/modules";

import * as ReactGA from "react-ga";

// import Countdown from 'on-react-countdown';

import CardCompany, {
    CardCompanyMobile
} from "./companies/card/CardCompany";

import FormTesting from "../../../testing/FormTesting";

import Names from "../../../../configs/application/strings/landing/bcf/companies/Names";
import DescriptionsShort from "../../../../configs/application/strings/landing/bcf/companies/DescriptionsShort";
import DescriptionsLong from "../../../../configs/application/strings/landing/bcf/companies/DescriptionsLong";


const BlockBunchBusinessFinance = ({
                                 videoId,
                                 isTranslationOn,
                             }) => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    if (isTabletOrMobileDevice)
        return (
