import React from 'react';

import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import FormControl from '@material-ui/core/FormControl';


const SelectOption = (props) => {
    return (
        <FormControl
            id="course-select-course-form-2-mobile"
            className="checkbox-form-vacancies no-label-margin"
        >
            {props.content.map((option, idx) => (
                <FormControlLabel
                    control={
                        <Checkbox
                            name={option.name}
                            disableRipple="true"
                            disableFocusRipple="true"
                            checked={option.value}
                            onChange={evt => props.onChange(evt, idx)}
                            value={option.name}
                        />
                    }
                    label={option.name}
                />
            ))}
        </FormControl>
    );
};

const Option = (props) => {
    const {
        required,
        question,
        description,
        activeDescriptionText
    } = props;

    return (
        <div>
            <div className="input-title-apply">
                {question}
                {
                    required
                        ? <div className="required-mark">*</div>
                        : null
                }
            </div>
            {
                activeDescriptionText
                    ? <p className="short-description-student">
                        {description}
                      </p>
                    : null
            }
            <div className="row">
                <div className="col-12 margin-bottom--10">
                    <SelectOption
                        {...props}
                    />
                </div>
            </div>
        </div>
    );
};


export {
    SelectOption,
    Option as default
};
