import React from 'react';

import {
    useMediaQuery
} from "react-responsive";

import srcSteel from "../../../../../images/landings/metinvest/blog/steel.svg";
import srcMetal from "../../../../../images/landings/metinvest/blog/metal.svg";
import srcTesla from "../../../../../images/landings/metinvest/blog/tesla.svg";

import { Element } from "react-scroll/modules";

import CardArticle from "./CardArticle";

const BlockArticles = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    if (isTabletOrMobileDevice)
        return (
            <Element
                id="metinvest-blog"
                name="metinvest-blog"
                className="element">
                <div className="container-fluid padding-tb-40">
                    <div className="col-12 col-lg-12">
                        <div className="row">
                            <div className="col-12 text-center">
                                <span className="landing-metinvest-header-mobile font-weight-500">Корисні статті</span>
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <span className="landing-metinvest-articles-subheader font-weight-400">
                                    Дізнавайся більше про сферу гірничо-металургійної діяльності разом із ексклюзивними матеріалами від компанії.
                                </span>
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <CardArticle
                                    src={srcSteel}
                                    date={"08.06.2020"}
                                    title={"Оцинкована сталь: як виробляють і де використовують"}
                                    url={"https://metinvestholding.com/ru/media/article/ocinkovannaya-stalj-kak-proizvodyat-i-gde-ispoljzuyut"}
                                />
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <CardArticle
                                    src={srcTesla}
                                    date={"02.09.2020"}
                                    title={"Сталь для Ілона Маска: секрети надміцного Cybertruck"}
                                    url={"https://metinvestholding.com/ru/media/article/stalj-dlya-ilona-maska-sekreti-sverhprochnogo-cybertruck"}
                                />
                            </div>
                        </div>

                        <div className="row margin-top-20">
                            <div className="col-12 text-center">
                                <CardArticle
                                    src={srcMetal}
                                    date={"12.08.2020"}
                                    title={"Найміцніші метали на Землі. Їх види та використання"}
                                    url={"https://metinvestholding.com/ru/media/article/samie-prochnie-metalli-na-zemle"}
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </Element>
        );

    return (
        <Element
            id="metinvest-blog"
            name="metinvest-blog"
            className="element">
            <div className="container-fluid padding-tb-60">
                <div className="col-12 col-lg-12">
                    <div className="row">
                        <div className="col-2"/>
                        <div className="col-8 text-center">
                            <span className="landing-metinvest-articles-header font-weight-600">Корисні статті</span>
                        </div>
                        <div className="col-2"/>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-2"/>
                        <div className="col-8 text-center">
                            <span className="landing-metinvest-articles-subheader font-weight-400">
                                Дізнавайся більше про сферу гірничо-металургійної діяльності разом із ексклюзивними матеріалами від компанії.
                            </span>
                        </div>
                        <div className="col-2"/>
                    </div>

                    <div className="row margin-top-60">
                        <div className="col-4 text-center">
                            <CardArticle
                                src={srcSteel}
                                date={"08.06.2020"}
                                title={"Оцинкована сталь: як виробляють і де використовують"}
                                url={"https://metinvestholding.com/ru/media/article/ocinkovannaya-stalj-kak-proizvodyat-i-gde-ispoljzuyut"}
                            />
                        </div>
                        <div className="col-4 text-center">
                            <CardArticle
                                src={srcTesla}
                                date={"02.09.2020"}
                                title={"Сталь для Ілона Маска: секрети надміцного Cybertruck"}
                                url={"https://metinvestholding.com/ru/media/article/stalj-dlya-ilona-maska-sekreti-sverhprochnogo-cybertruck"}
                            />
                        </div>
                        <div className="col-4 text-center">
                            <CardArticle
                                src={srcMetal}
                                date={"12.08.2020"}
                                title={"Найміцніші метали на Землі. Їх види та використання"}
                                url={"https://metinvestholding.com/ru/media/article/samie-prochnie-metalli-na-zemle"}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </Element>
    );
};

export {
    BlockArticles as default
};
