import React from "react";

import srcCloud from "../../../../../images/icons/modified/cloud.svg";
import srcTelegram from "../../../../../images/icons/modified/telegram-new.svg";
import srcInstagram from "../../../../../images/icons/modified/instagram-new.svg";

import { Link } from "react-router-dom";

import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';

import * as ReactGA from "react-ga";


const BlockResumeUploaded = ({
                                 isTabletOrMobileDevice,
                                 // Events
                                 onClickFileUpload
                             }) => {
    if (isTabletOrMobileDevice)
        return (
            <button
                className={"landing-registration-button-resume-mobile font-weight-500"}
                onClick={onClickFileUpload}>
                <img
                    src={srcCloud}
                    className="landing-registration-button-resume-icon margin-right-10"
                    alt={"Іконка резюме"}
                /> Завантажити резюме
            </button>
        );

    return (
        <button
            className={"landing-registration-button-resume font-weight-500"}
            onClick={onClickFileUpload}>
            <img
                src={srcCloud}
                className="landing-registration-button-resume-icon margin-right-10"
                alt={"Іконка резюме"}
            /> Завантажити резюме
        </button>
    );
};

const BlockResumeNotUploaded = ({
                                    fileName,
                                    isTabletOrMobileDevice,
                                    // Events
                                    onClickFileUpload,
                                    onClickFileRemove
                                }) => {
    if (isTabletOrMobileDevice)
        return (
            <div>
                <button
                    className={"landing-registration-button-resume-mobile font-weight-500"}
                    onClick={onClickFileUpload}>
                    <CheckCircleOutlineIcon
                        className="mr-2"/>
                    {fileName.length > 15 ? `${fileName.slice(0, 15)} ...` : fileName}
                </button>

                <div className="text-center">
                    <a
                        href={null}
                        className="mt-4"
                        style={{
                            cursor: "pointer"
                        }}
                        onClick={onClickFileRemove}>
                        <u>Скасувати</u>
                    </a>
                </div>
            </div>
        );

    return (
        <div>
            <button
                className={"landing-registration-button-resume font-weight-500"}
                onClick={onClickFileUpload}>
                <CheckCircleOutlineIcon
                    className="mr-2"/>
                {fileName.length > 15 ? `${fileName.slice(0, 15)} ...` : fileName}
            </button>

            <a
                href={null}
                className="ml-4"
                style={{
                    cursor: "pointer"
                }}
                onClick={onClickFileRemove}>
                <u>Скасувати</u>
            </a>
        </div>
    );
};


const BlockResume = ({
                         // Data
                         fileName,
                         isUploaded,
                         // Event
                         onClickFileRemove,
                         onClickFileUpload,
                         isTabletOrMobileDevice
                     }) => {
    if (!isUploaded)
        return (
            <BlockResumeUploaded
                isTabletOrMobileDevice={isTabletOrMobileDevice}
                onClickFileUpload={onClickFileUpload}
            />
        );

    return (
        <BlockResumeNotUploaded
            fileName={fileName}
            isTabletOrMobileDevice={isTabletOrMobileDevice}
            onClickFileUpload={onClickFileUpload}
            onClickFileRemove={onClickFileRemove}
        />
    );
};


const BlockSocialNetworks = ({
                                 isTabletOrMobileDevice
                             }) => {
    if (isTabletOrMobileDevice)
        return (
            <div className="row text-center margin-top-40">
                <div className="col-12 text-center">
                    <p className="landing-registration-p-text font-weight-500">
                        Обов’язково{" "}
                        <span className="landing-registration-p-text-underline">
                            <a
                                target="_blank"
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Bunch Marketing',
                                        action: `Click Button`,
                                        label: 'Telegram Channel @bcfo_chat',
                                    });
                                }}
                                href="https://t.me/bigcareerfest"
                                className="bunch-bcfo-a">
                                підписуйся
                            </a>
                        </span>, щоб бути в курсі всіх апдейтів!
                    </p>
                </div>

                <div className="col-12">
                    <Link
                        className="mr-2"
                        target="_blank"
                        onClick={evt => {
                            ReactGA.event({
                                category: 'Landing BCF',
                                action: 'Click Telegram',
                                label: 'Block Registration',
                            });
                        }}
                        to={{
                            pathname: "https://rebrand.ly/nrlflkb"
                        }}>
                        <img
                            src={srcTelegram}
                            className="icon-block"
                            alt={"Телеграм"}
                        />
                    </Link>

                    <Link
                        className="ml-2"
                        target="_blank"
                        onClick={evt => {
                            ReactGA.event({
                                category: 'Landing BCF',
                                action: 'Click Instagram',
                                label: 'Block Registration',
                            });
                        }}
                        to={{
                            pathname: "https://rebrand.ly/6dtmyyi"
                        }}>
                        <img
                            src={srcInstagram}
                            className="icon-block"
                            alt={"Інстаграм"}
                        />
                    </Link>
                </div>
            </div>
        );

    return (
        <div className="row text-center margin-top-40">
            <div className="col-1"/>

            <div className="col-6 text-justify">
                <p className="landing-registration-p-text font-weight-500">
                    Обов’язково{" "}
                    <span className="landing-registration-p-text-underline">
                        <a
                            target="_blank"
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF',
                                    action: 'Click Telegram',
                                    label: 'Block Registration',
                                });
                            }}
                            href="https://rebrand.ly/nrlflkb"
                            className="bunch-bcfo-a">
                            підписуйся
                        </a>
                    </span>, щоб бути в курсі всіх апдейтів!
                </p>
            </div>

            <div className="col-5">
                <Link
                    className="mr-4"
                    target="_blank"
                    onClick={evt => {
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Telegram',
                            label: 'Block Registration',
                        });
                    }}
                    to={{
                        pathname: "https://rebrand.ly/nrlflkb"
                    }}>
                    <img
                        src={srcTelegram}
                        className="icon-block"
                        alt={"Телеграм"}
                    />
                </Link>

                <Link
                    className="ml-2 margin-right-80"
                    target="_blank"
                    onClick={evt => {
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Instagram',
                            label: 'Block Registration',
                        });
                    }}
                    to={{
                        pathname: "https://rebrand.ly/6dtmyyi"
                    }}>
                    <img
                        src={srcInstagram}
                        className="icon-block"
                        alt={"Інстаграм"}
                    />
                </Link>
            </div>
        </div>
    );
};


export {
    BlockResume,
    BlockSocialNetworks
};
