import React from 'react';

import { Link } from "react-router-dom";

import * as ReactGA from "react-ga";


const CardBunch = ({
                           src,
                           title,
                           link,
                           schedule,
                           description,
                           // Color gamma parameters
                           cssColorBackground,
                       }) => {
    return (
        <div
            className="row row-center">
            <div className="col-lg-12">
                <a
                    onClick={evt =>
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Card',
                            label: 'Block Bunches'
                        })
                    }
                    className="link-activities"
                    href={link}>
                    <div
                        className={`landing-bunches-card-frame block-activities ${cssColorBackground}`}>
                        <div className="margin-bottom-15">
                            <img
                                className="landing-bunches-card-icon"
                                src={src}
                                alt={"Банчі"}
                            />
                            <span className="landing-bunches-card-click-title landing-bunches-card-click-title-underscore ml-2 font-weight-400">Тисни та дізнавайся більше про...</span>
                        </div>
                        <p className="activities-title font-weight-500">
                            {title}
                        </p>
                        <p className="activities-exaplanation font-weight-400">
                            {description}
                        </p>
                        <div className="row margin-top-5">
                            <div className="col-12">
                                <ul className="landing-bunches-list">
                                    {schedule.map(timing => {
                                        return <li className="landing-bunches-li">{timing}</li>
                                    })}
                                </ul>
                            </div>
                        </div>
                    </div>
                </a>
            </div>
        </div>
    );
};

const CardBunchMobile = ({
                             src,
                             title,
                             link,
                             schedule,
                             description,
                             // Color gamma parameters
                             cssColorBackground,
                         }) => {
    return (
        <div
            className="row row-center margin-top-10">
            <div className="col-lg-12">
                <a
                    onClick={evt =>
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Card',
                            label: 'Block Bunches'
                        })
                    }
                    className="link-activities"
                    href={link}>
                    <div className={`landing-bunches-card-frame-mobile ${cssColorBackground}`}>
                        <div className="margin-bottom-15">
                            <img
                                className="landing-bunches-card-icon"
                                src={src}
                                alt={"Банчі"}
                            />
                            <span className="landing-bunches-card-click-title-mobile landing-bunches-card-click-title-underscore ml-2 font-weight-400">Тисни, дізнавайся більше про...</span>
                        </div>
                        <p className="activities-title font-weight-500">
                            {title}
                        </p>
                        <p className="activities-exaplanation font-weight-400">
                            {description}
                        </p>
                        <div className="row margin-top-5">
                            <div className="col-12">
                                <ul className="landing-bunches-list">
                                    {schedule.map(timing => {
                                        return <li className="landing-bunches-li">{timing}</li>
                                    })}
                                </ul>
                            </div>
                        </div>
                    </div>
                </a>
            </div>
        </div>
    );
};

export {
    CardBunchMobile,
    CardBunch as default
};
