import React from 'react';

import menu from "../../images/icons/modified/menu.svg";

import {
    withRouter
} from "react-router-dom";

import {
    DirectLink
} from 'react-scroll';


class NavBarActivities extends React.Component {
    constructor(props) {
        super(props);
    }

    handleClick = (action) => {
        if (this.props.location.pathname !== "/") {
            this.props.history.push(action);
            window.location.reload();
        } else {
            this.props.history.push(action);
        }
    };

    render() {
        return (
            <nav className="navbar navbar-expand-lg">
                <a
                    className="navbar-brand font-weight-500"
                    href="/">
                    Big Career Fest. Online
                </a>
            </nav>
        );
    }
}

export default withRouter(NavBarActivities);
