import React from "react";

import srcClickMe from "../../../../../images/icons/modified/click.svg";

import { useMediaQuery } from "react-responsive";

import CardBunch, { CardBunchMobile } from "./card/CardBunch";
import {Element} from "react-scroll/modules";


const BlockBunches = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    if (isTabletOrMobileDevice)
        return (
            <Element
                id="bunches"
                name="bunches"
                className="element">
                <div className="container-fluid padding-tb-40">
                    <div className="row">
                        <div className="col-12 text-center">
                        <span
                            className="landing-bunches-header-mobile font-weight-600">
                            –ë–∞–Ω—á—ñ –ª–µ–∫—Ü—ñ–π
                        </span>
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12 text-center">
                            <span
                                className="landing-bunches-subheader font-weight-400">
                                –û–±–∏—Ä–∞–π —Å–ø–µ—Ü—ñ–∞–ª—å–Ω—ñ—Å—Ç—å, –ø—Ä–æ—Ö–æ–¥—å 2-–¥–µ–Ω–Ω–∏–π –∫—É—Ä—Å —Ç–∞ –æ—Ç—Ä–∏–º—É–π —Å–µ—Ä—Ç–∏—Ñ—ñ–∫–∞—Ç –≤—ñ–¥ —Ç–æ–ø–æ–≤–∏—Ö –∫–æ–º–ø–∞–Ω—ñ–π!
                            </span>
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12">
                            <CardBunchMobile
                                src={srcClickMe}
                                title={"–ë–∞–Ω—á ¬´–ú–∞—Ä–∫–µ—Ç–∏–Ω–≥¬ª"}
                                description={"–ù–µ–æ–±—Ö—ñ–¥–Ω—ñ —Ö–∞—Ä–¥ —Å–∫—ñ–ª–∏ –∑—ñ —Å—Ñ–µ—Ä–∏ –º–∞—Ä–∫–µ—Ç–∏–Ω–≥—É? –í–∏–±—É—Ö–æ–≤—ñ –ª–µ–∫—Ü—ñ—ó –∑ –±–∞–Ω—á—É –≤–∂–µ —Ç—É—Ç!"}
                                schedule={[
                                    "üïï 26 –≤–µ—Ä–µ—Å–Ω—è, 12:00-13:00, Dentsu Aegis Network",
                                    "üïù 27 –≤–µ—Ä–µ—Å–Ω—è, 14:00-15:00, –Ü–Ω—Ç–µ—Ä—Ç–æ–ø",
                                    "üïü 27 –≤–µ—Ä–µ—Å–Ω—è, 16:00-17:00, Genesis"
                                ]}
                                link={"/bunch-marketing"}
                                cssColorBackground={"landing-bunches-card-marketing"}
                            />
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12">
                            <CardBunchMobile
                                src={srcClickMe}
                                title={"–ë–∞–Ω—á ¬´Communication¬ª"}
                                description={"–ö–ª—ñ–∫–∞–π —Å—é–¥–∏, —è–∫—â–æ –ª—é–±–∏—à —Å—Ñ–µ—Ä—É –∫–æ–º—É–Ω—ñ–∫–∞—Ü—ñ–π! –ù–∞ —Ç–µ–±–µ —á–µ–∫–∞—î —â–æ—Å—å –∫—Ä—É—Ç–µ"}
                                schedule={[
                                    "üï¶ 26 –≤–µ—Ä–µ—Å–Ω—è, 13:00-14:00, Metinvest",
                                    "üïí 26 –≤–µ—Ä–µ—Å–Ω—è, 15:00-16:00, JTI",
                                    "üïï 27 –≤–µ—Ä–µ—Å–Ω—è, 12:00-13:00, BAT",
                                    "üïí 27 –≤–µ—Ä–µ—Å–Ω—è, 15:00-16:00, Coca-Cola",
                                ]}
                                link={"/bunch-communication"}
                                cssColorBackground={"landing-bunches-card-communication"}
                            />
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12">
                            <CardBunchMobile
                                src={srcClickMe}
                                title={"–ë–∞–Ω—á ¬´Business & Finance¬ª"}
                                description={"–•–æ—á–µ—à –Ω–∞–±—É—Ç–∏ –∫—Ä—É—Ç—ñ —Ö–∞—Ä–¥ —Å–∫—ñ–ª–∏ –∑—ñ —Å—Ñ–µ—Ä–∏ –±—ñ–∑–Ω–µ—Å—É —Ç–∞ —Ñ—ñ–Ω–∞–Ω—Å—ñ–≤?! –¢–æ–±—ñ —Å—é–¥–∏!"}
                                schedule={[
                                    "üï¶ 26 –≤–µ—Ä–µ—Å–Ω—è, 11:00-12:00, KPMG",
                                    "üïù 26 –≤–µ—Ä–µ—Å–Ω—è, 14:00-15:00, Baker Tilly",
                                    "üïê 27 –≤–µ—Ä–µ—Å–Ω—è, 13:00-14:00, Roland Berger",
                                ]}
                                link={"/bunch-business-finance"}
                                cssColorBackground={"landing-bunches-card-business-finance"}
                            />
                        </div>
                    </div>
                </div>
            </Element>
        );

    return (
        <Element
            id="bunches"
            name="bunches"
            className="element">
            <div className="container-fluid padding-tb-60">
                <div className="row">
                    <div className="col-12 text-center">
                        <span
                            className="landing-metinvest-header font-weight-600">
                            –ë–∞–Ω—á—ñ –ª–µ–∫—Ü—ñ–π
                        </span>
                    </div>
                </div>

                <div className="row margin-top-20">
                    <div className="col-12 text-center">
                        <span
                            className="landing-metinvest-subheader font-weight-400">
                            –û–±–∏—Ä–∞–π —Å–ø–µ—Ü—ñ–∞–ª—å–Ω—ñ—Å—Ç—å, –ø—Ä–æ—Ö–æ–¥—å 2-–¥–µ–Ω–Ω–∏–π –∫—É—Ä—Å —Ç–∞ –æ—Ç—Ä–∏–º—É–π —Å–µ—Ä—Ç–∏—Ñ—ñ–∫–∞—Ç –≤—ñ–¥ —Ç–æ–ø–æ–≤–∏—Ö –∫–æ–º–ø–∞–Ω—ñ–π!
                        </span>
                    </div>
                </div>

                <div className="row margin-top-40">
                    <div className="col-4">
                        <CardBunch
                            src={srcClickMe}
                            title={"–ë–∞–Ω—á ¬´–ú–∞—Ä–∫–µ—Ç–∏–Ω–≥¬ª"}
                            description={"–ù–µ–æ–±—Ö—ñ–¥–Ω—ñ —Ö–∞—Ä–¥ —Å–∫—ñ–ª–∏ –∑—ñ —Å—Ñ–µ—Ä–∏ –º–∞—Ä–∫–µ—Ç–∏–Ω–≥—É? –í–∏–±—É—Ö–æ–≤—ñ –ª–µ–∫—Ü—ñ—ó –∑ –±–∞–Ω—á—É –≤–∂–µ —Ç—É—Ç!"}
                            schedule={[
                                "üïï 26 –≤–µ—Ä–µ—Å–Ω—è, 12:00-13:00, Dentsu Aegis Network",
                                "üïù 27 –≤–µ—Ä–µ—Å–Ω—è, 14:00-15:00, –Ü–Ω—Ç–µ—Ä—Ç–æ–ø",
                                "üïü 27 –≤–µ—Ä–µ—Å–Ω—è, 16:00-17:00, Genesis"
                            ]}
                            link={"/bunch-marketing"}
                            cssColorBackground={"landing-bunches-card-marketing"}
                        />
                    </div>
                    <div className="col-4">
                        <CardBunch
