import React from 'react';

import { withRouter } from "react-router-dom";
import * as ReactGA from "react-ga";

class NavBar extends React.Component {
    constructor(props) {
        super(props);
    }

    handleClick = (action) => {
        if (this.props.location.pathname !== "/") {
            this.props.history.push(action);
            window.location.reload();
        } else {
            this.props.history.push(action);
        }
    };

    render() {
        return (
            <nav className="navbar navbar-expand-lg">
                <a
                    href="/"
                    className="navbar-brand font-weight-500"
                    onClick={evt => {
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Big Career Fest. Online',
                            label: 'NavBar'
                        });
                    }}>
                    Big Career Fest. Online
                </a>
                <button
                    className="navbar-toggler"
                    type="button"
                    data-toggle="collapse"
                    data-target="#navbarText"
                    aria-controls="navbarText"
                    aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon">
                        <img
                            src={srcMenu}
                            className="menu-toggler"/>
                    </span>
                </button>

                <div
                    className="collapse navbar-collapse"
                    id="navbarText">
                    <ul className="navbar-nav ml-auto mr-auto"/>
                    <ul className="navbar-nav">
                        <Link
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing BCF',
                                    action: 'Click Get Job',
                                    label: 'NavBar'
                                });
                            }}
                            activeClass="active"
                            className="test2"
                            to="registration"
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <li
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-item nav-item-text font-weight-400"
                                        : "nav-item nav-item-text font-weight-500"
                                }>
                                Отримати роботу
                            </li>
                        </Link>
                    </ul>

                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="activities"
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click Activities',
                                        label: 'NavBar'
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Активності
                            </a>
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to={"companies"}
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF / NavBar',
                                        action: 'Click Companies',
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Компанії
                            </a>
                        </Link>
                    </li>
                    </ul>
                </div>
            </nav>
        );
    }
}

export default withRouter(NavBar);
