import React from 'react';

import {
    useMediaQuery
} from 'react-responsive';

import srcStart from "../../../../images/landings/bcf/greetings/start.svg";
import srcLearn from "../../../../images/landings/bcf/learn.svg"

import * as ReactGA from "react-ga";

import {
    Link
} from "react-scroll/modules";


const BlockGreetings = () => {
    return (
        <div className="container padding-landing-top">
            <div className="row">
                <div className="col-12 col-lg-6 text-column">
                    <div className="row">
                        <div className="col-12">
                            <p className="main-title">Big Career Fest кар'єрний івент в Україні</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12">
                            <p className="title-small">Фест почався! Бери участь у розіграшах, кейсах та інших активностях.</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12 col-lg-6">
                            <Link
                                activeClass="active"
                                to="activities"
                                spy={true}
                                smooth={true}
                                offset={50}
                                duration={500}>
                                <button
                                    className="button-registrate-title">
                                    Активності
                                </button>
                            </Link>
                        </div>
                        <div className="col-12 col-lg-6">
                            <Link
                                activeClass="active"
                                to="lectures"
                                spy={true}
                                smooth={true}
                                offset={50}
                                duration={500}>
                                <button className="button-more-title">
                                    Лекції
                                </button>
                            </Link>
                        </div>
                    </div>
                </div>
                <div className="col column-desktop"/>
                <div className="col-12 col-lg-6 justify-content-end">
                    <img
                        src={srcLearn}
                        className="think-image"
                        alt={"Кар'єрний фестивль"}
                    />
                </div>
            </div>
        </div>
    );
};

const BlockGreetingsRegistration = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    if (isTabletOrMobileDevice)
        return (
            <div className="container margin-top-80 margin-bottom-40">
                <div className="row">
                    <div className="col-12">
                        <div className="row">
                            <div className="col-12">
                                <p className="font-weight-400">
                                    <div className="landing-greetings-start text-center">
                                        <img
                                            className="mr-2"
                                            src={srcStart}
                                            alt="Фест розпочався"
                                        />
                                        {/*<span className="landing-greetings-start-text font-weight-500">*/}
                                            {/*ФЕСТ РОЗПОЧАВСЯ*/}
                                        {/*</span>*/}
                                        <span className="landing-greetings-start-text font-weight-500">
                                            ФЕСТ ЗАВЕРШИВСЯ
                                        </span>
                                    </div>
                                </p>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-12">
                                <p className="landing-greetings-title-mobile font-weight-500">
                                    Big Career Fest — фестиваль <AnimatedTypingComponent/> для студентів
                                </p>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-6">
                                <Link
                                    to="bunches"
                                    spy={true}
                                    smooth={true}
                                    offset={50}
                                    activeClass="active"
                                    duration={1000}>
                                    <button
                                        onClick={evt =>
