import React from 'react';

import {
    useMediaQuery
} from "react-responsive";

import srcBook from "../../../../images/landings/metinvest/book/book.png";

const BlockBook = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    if (isTabletOrMobileDevice)
        return (
            <div className="container-fluid padding-tb-40">
                <div className="col-12 col-lg-12">
                    <div className="row">
                        <div className="col-12 text-center">
                            <span className="landing-metinvest-header-mobile font-weight-500">Книга “Металургійна кухня”</span>
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12 text-center">
                            <img
                                src={srcBook}
                                className="landing-metinvest-book-image-mobile"
                                alt={"Книга Металургійна кухня"}
                            />
                        </div>
                    </div>

                    <div className="row margin-top-20">
                        <div className="col-12 text-left">
                            <p className="landing-metinvest-book-text margin-top-20 font-weight-400">
                                Перед тобою — книга про гірничу справу та металургію. Незвичайна і проста водночас. Тут немає кілометрів формул і вагонів термінів. Лише найнеобхідніше і доступно написано, мало тексту й багато картинок, кумедні випадки та цікаві факти. Все це для того, щоб кожна, навіть далека від промисловості людина, могла зрозуміти, як усе влаштовано на металургійній «кухні».
                            </p>
                        </div>
                    </div>

                    <div className="row margin-top-15">
                        <div className="col-12 text-center">
                            <a
                                href={"http://metinvest.tilda.ws/steelmaking"}
                                target={"_blank"}
                                className="btn landing-metinvest-book-button font-weight-500">
                                Читати
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        );

    return (
        <div className="container-fluid padding-tb-60">
            <div className="col-12 col-lg-12">
                <div className="row">
                    <div className="col-2"/>
                    <div className="col-8 text-center">
                        <span className="landing-metinvest-book-header font-weight-600">Книга “Металургійна кухня”</span>
                    </div>
                    <div className="col-2"/>
                </div>

                <div className="row margin-top-40">
                    <div className="col-6 text-center">
                        <img
                            src={srcBook}
                            className="landing-metinvest-book-image"
                            alt={"Книга Металургійна кухня"}
                        />
                    </div>

                    <div className="col-6 text-left margin-top-80">
                        <p className="landing-metinvest-book-text margin-top-20 font-weight-400">
                            Перед тобою — книга про гірничу справу та металургію. Незвичайна і проста водночас. Тут немає кілометрів формул і вагонів термінів. Лише найнеобхідніше і доступно написано, мало тексту й багато картинок, кумедні випадки та цікаві факти. Все це для того, щоб кожна, навіть далека від промисловості людина, могла зрозуміти, як усе влаштовано на металургійній «кухні».
                        </p>

                        <a
                            href={"http://metinvest.tilda.ws/steelmaking"}
                            target={"_blank"}
                            className="btn landing-metinvest-book-button font-weight-500">
                            Читати
                        </a>
                    </div>
                </div>
            </div>
        </div>
    );
};

export {
    BlockBook as default
};
