import React from "react";

import jcc from "../../../../../images/logotypes/partners/jcc.jpg";
import highers from "../../../../../images/landings/bcf/bcf.png";

import {
    useMediaQuery
} from "react-responsive";

import CardOrganizators from "./CardOrganizators";


const BlockOrganizators = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    return (
        <div className={
            isTabletOrMobileDevice
                ? "container background-white padding-top-bottom-60"
                : "container background-white padding-tb-60 margin-bottom-80"
        }>
            <div className="row">
                <div className="col-12">
                    <p className={
                        isTabletOrMobileDevice
                            ? "landing-organizators-title-mobile font-weight-600"
                            : "landing-organizators-title font-weight-600"
                    }>
                        Хто працює над фестом?
                    </p>
                </div>
            </div>

            <div className="row row-center">
                <div className="col-12 col-lg-10">
                    <p className="title-block-expl font-weight-400">
                        Знайомся ближче із командою, яка влаштовує для тебе цей неймовірний івент!
                    </p>
                </div>
            </div>

            <div className="row row-center">
                <CardOrganizators
                    image={jcc}
                    title={"Центр кар'єри та працевлаштування студентів та випускників"}
                    subTitle={"Національного університету 'Києво-Могилянська Академія'"}
                    buttonLink={"https://t.me/jccnaukma"}
                    buttonClass={"button-naukma"}
                    explanation={"Перший в Україні центр системної підтримки студентів та випускників в їхньому професійному становленні. Майстер класи від профі, освітні проєкти від топових компаній, вакансії та програми стажування - все це на нашому каналі. Використовуйте кожну можливість!"}
                />

                <div className="between-column"/>

                <CardOrganizators
                    image={highers}
                    title={"Платформа з працевлаштування студентів Highers"}
                    subTitle={"Найкраща робота для найкращих студентів"}
                    buttonLink={"https://t.me/higherspro"}
                    buttonClass={"button-highers"}
                    explanation={"Ми збираємо найкращі кар'єрні можливості для студентів від топових компаній України. Також ми допомагаємо створювати резюме та надаємо консультації щодо проходження співбесід. Підписуйся на наш канал та будуй успішну кар'єру разом із нами!"}
                />
            </div>
        </div>
    );
};


export {
    BlockOrganizators as default
};
