import React from 'react';
import { Link } from 'react-scroll';
import education1 from '../images/education-1.png'
import education2 from '../images/education-2.png'
import education3 from '../images/education-3.png'

class ShortTextEducation extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <React.Fragment>
                <div className="col-6 education-structure-subblock">
                    <p className="title-text-subblock">
                        {this.props.title}
                    </p>
                    <p className="secondary-text-subblock">
                        {this.props.shortDescription}
                    </p>
                </div>
                <div className="col-3">
                    <div className="image-education-block">
                    </div>
                </div>
            </React.Fragment>

        );
    }
}

export default ShortTextEducation;
