import React from 'react';

import {
    useMediaQuery
} from "react-responsive";

import { Link } from "react-scroll/modules";

import srcMetinvest from '../../../../images/landings/metinvest/metinvest-logo.png';

import * as ReactGA from "react-ga";

const BlockGreetings = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );

    if (isTabletOrMobileDevice)
        return (
            <div className="container-fluid landing-metinvest-background-mobile">
                <div className="col-12 col-lg-12">
                    <div className="row">
                        <div className="col-12 text-center">
                            <img
                                src={srcMetinvest}
                                className="landing-metinvest-logo-mobile"
                                alt="Metinvest Background"
                            />
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-12 text-center">
                            <p className="landing-metinvest-greetings-text margin-top-20 font-weight-500">
                                Гідна робота в рідній країні.
                                <br/>
                                У нас працює понад 80 тисяч співробітників на підприємствах в Україні, Італії, Великій Британії, Болгарії та США.
                            </p>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-12 text-center">
                            <Link
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing Metinvest',
                                        action: 'Click Registration',
                                        label: 'Block Greetings'
                                    });
                                }}
                                activeClass="active"
                                to="metinvest-registration"
                                spy={true}
                                smooth={true}
                                duration={2000}
                            >
                                <button
                                    className="btn landing-metinvest-greetings-button font-weight-500">
                                    Хочу працевлаштуватись
                                </button>
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        );

    return (
        <div className="container-fluid landing-metinvest-background">
            <div className="col-12 col-lg-12">
                <div className="row">
                    <div className="col-2"/>
                    <div className="col-8 text-center">
                        <img
                            src={srcMetinvest}                                                      className="landing-metinvest-logo"
                            alt="Metinvest Background"
                        />
                    </div>
                    <div className="col-2"/>
                </div>

                <div className="row">
                    <div className="col-2"/>
                    <div className="col-8 text-center">
                        <p className="landing-metinvest-greetings-text margin-top-20 font-weight-500">
                            Гідна робота в рідній країні.
                            <br/>
                            У нас працює понад 80 тисяч співробітників на підприємствах в Україні, Італії, Великій Британії, Болгарії та США.
                        </p>
                    </div>
                    <div className="col-2"/>
                </div>

                <div className="row">
                    <div className="col-4"/>
                    <div className="col-4 text-center">
                        <Link
                            onClick={evt => {
                                ReactGA.event({
                                    category: 'Landing Metinvest',
                                    action: 'Click Registration',
                                    label: 'Block Greetings'
                                });
                            }}
                            activeClass="active"
                            to="metinvest-registration"
                            spy={true}
                            smooth={true}
                            duration={2000}
                        >
                            <button
                                className="btn landing-metinvest-greetings-button font-weight-500">
                                Хочу працевлаштуватись
                            </button>
                        </Link>
                    </div>
                    <div className="col-4"/>
                </div>
            </div>
        </div>
    );
};

export {
    BlockGreetings as default
};
