import React from 'react';

import { Link } from "react-router-dom";

import * as ReactGA from "react-ga";

import srcClick from "../../../../../../images/icons/modified/click.svg";


const CardMetinvest = ({
                           src,
                           title,
                           link,
                           description,
                           buttonText,
                           // Color gamma parameters
                           cssColorBackground,
                           cssColorButton
                       }) => {
    return (
        <div
            className="row row-center">
            <div className="col-lg-12">
                <a
                    onClick={evt =>
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: `Click ${title}`,
                            label: 'Block Metinvest'
                        })
                    }
                    target={"_blank"}
                    className="link-activities"
                    href={link}>
                    <div className={`landing-metinvest-card-frame ${cssColorBackground}`}>
                        <div className="icon-margin-block">
                            <div className="row">
                                <div className="col-2 text-left">
                                    <img
                                        className={"landing-metinvest-icon"}
                                        src={src}
                                        alt={"Metinvest"}
                                    />
                                </div>
                                <div className="col-10 text-right">
                                    <span className="landing-bunches-li mr-2">
                                        <span className="landing-metinvest-card-click-title-underscore font-weight-400">
                                            Дізнатись більше
                                        </span>
                                    </span>
                                    <img
                                        className="landing-activity-icon"
                                        alt={"Кейси"}
                                        src={srcClick}
                                    />
                                </div>
                            </div>
                        </div>
                        <p className="activities-title font-weight-500">
                            {title}
                        </p>
                        <p className="activities-exaplanation font-weight-400">
                            {description}
                        </p>
                    </div>
                </a>
            </div>
        </div>
    );
};

/*
<Link
    target={"_blank"}
    to={{
        pathname: link
    }}>
    <button
        onClick={evt => {
            ReactGA.event({
                category: 'Landing BCF',
                action: 'Click Registration / Metinvest',
                label: 'Block Metinvest'
            });
        }}
        className={`landing-metinvest-card-button ${cssColorButton} font-weight-500`}>
        {buttonText}
    </button>
</Link>
*/

const CardMetinvestMobile = ({
                                 src,
                                 title,
                                 link,
                                 description,
                                 buttonText,
                                 // Color gamma parameters
                                 cssColorBackground,
                                 cssColorButton
                       }) => {
    return (
        <div
            className="row row-center">
            <div className="col-lg-12">
                <a
                    onClick={evt =>
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: `Click ${title}`,
                            label: 'Block Metinvest'
                        })
                    }
                    target={"_blank"}
                    className="link-activities"
                    href={link}>
                    <div className={`landing-metinvest-card-frame-mobile block-activities ${cssColorBackground}`}>
                        <div className="icon-margin-block">
                            <div className="row">
                                <div className="col-2 text-left">
                                    <img
                                        className={"landing-metinvest-icon"}
                                        src={src}
                                        alt={"Metinvest"}
                                    />
                                </div>
                                <div className="col-10 text-right">
                                    <span className="landing-bunches-li mr-2">
                                        <span className="landing-metinvest-card-click-title-underscore font-weight-400">
                                            Дізнатись більше
                                        </span>
                                    </span>
                                    <img
                                        className="landing-activity-icon"
                                        alt={"Кейси"}
                                        src={srcClick}
                                    />
                                </div>
                            </div>
                        </div>
                        <p className="activities-title font-weight-500">
                            {title}
                        </p>
                        <p className="activities-exaplanation font-weight-400">
                            {description}
                        </p>
                    </div>
                </a>
            </div>
        </div>
    );
};

/*
<Link
    target={"_blank"}
    to={{
        pathname: link
    }}>
    <button
        onClick={evt => {
            ReactGA.event({
                category: 'Landing BCF',
                action: 'Click Registration / Metinvest',
                label: 'Block Metinvest'
            });
        }}
        className={`landing-metinvest-card-button ${cssColorButton} font-weight-500`}>
        {buttonText}
    </button>
</Link>
*/

export {
    CardMetinvestMobile,
    CardMetinvest as default
};
