import React, { useState } from 'react';

// Animations
import {
    Animated
} from "react-animated-css";

import {
    useMediaQuery
} from "react-responsive";

// Import Date picker modules
import "react-datepicker/dist/react-datepicker.css";
import { registerLocale } from "react-datepicker";
import DatePicker from "react-datepicker";

import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from "@material-ui/core/Snackbar/Snackbar";

import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';

// Import Google Analytics module
import * as ReactGA from 'react-ga';

// Import Google Firebase module
import {
    storage,
    ResumesGenesis,
} from '../../../configs/google/firebase/config';

import { Link, Element } from 'react-scroll';

import uk from 'date-fns/locale/uk';

import CardGift from "./cards/CardGift";

import Footer from "../../footer/Footer";

import {
    InputTextArea,
    InputTextField,
} from "./inputs/InputText";

import BlockWinners from "./blocks/BlockWinners";

import axios from "axios";

const POST_REGISTRATION_REQUEST = '/registration/genesis';

registerLocale('uk', uk);


const Alert = (props) => {
    return <MuiAlert
        elevation={6}
        variant="filled"
        {...props} />;
};


const LandingGenesis = (props) => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    const [state, setState] = useState({
        // Error controllers
        errorStatus: null,
        errorFields: [],
        isSuccess: false,
    });

    const [application, setApplication] = useState({
        // Boolean triggers
        isUploaded: false,

        // User data
        ns: null,
        fs: null,
        email: null,
        phone: null,
        employment: null,

        file: null,
        fileName: null,
        fileData: null,

        confirm: true,

        dateBirth: new Date(),
        institute: "НУ Києво-Могилянська академія",
    });

    const getInitialState = () => {
        return {
            isError: false,
            errorMsg: null,
            errorStatus: null,
            errorFields: []
        };
    };

    const onSelectFile = () => {
        const selector = document.createElement('input');
        selector.setAttribute('type', 'file');
        selector.setAttribute('multiple', 'multiple');
        selector.onchange = evt =>
            setApplication({
                ...application,
                file: evt.target.files[0],
                isUploaded: true
            });
        return selector;
    };

    const fileSelector = onSelectFile();

    const asyncRegistrationRequest = async (params) => {
        try {
            const response = await axios.post(POST_REGISTRATION_REQUEST, {
                ...application,
                ...params
            });
            return response.data;
        } catch (err) {
            switch (err.response.status) {
                case 403:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: err.response.data.data.errors[0].msg,
                        errorStatus: err.response.status,
                        errorFields: err.response.data.data.errors
                            .map(errorObj => errorObj.param),
                    });
                    break;

                case 409:
                    setState({
                        ...state,
                        isError: true,
                        errorMsg: "Ви вже реєстувались ;3",
                        errorStatus: err.response.status,
                        errorFields: ["email"],
                    });
                    break;

                case 500:
                    setState({
                        ...state,
                        errorStatus: err.response.status,
                        errorFields: [],
                    });
                    break;
            }
            throw err;
        }
