                    // Trigger Google Analytics registration click
                    ReactGA.event({
                        category: 'Landing Genesis',
                        action: 'Submit Registration',
                        label: 'Block Registration',
                    });
                    // Initialize new empty state
                    setState({
                        ...state,
                        ...data,
                        ...getInitialState()
                    });
                }
            })();
        }
