import React from 'react';

import { Link as RouterLink } from "react-router-dom";

import {
    withRouter
} from "react-router-dom";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
} from 'react-scroll';

import * as ReactGA from "react-ga";

export default class NavBar extends React.Component {

    constructor(props) {
        super(props);

        this.listener = null;
        this.state = {
            status: "top"
        };
    }

    componentDidMount() {
        this.listener = document.addEventListener("scroll", e => {
            var scrolled = document.scrollingElement.scrollTop;
            if (scrolled >= 120) {
                if (this.state.status !== "amir") {
                    this.setState({ status: "amir" });
                }
            } else {
                if (this.state.status !== "top") {
                    this.setState({ status: "top" });
                }
            }
        });
    }

    componentDidUpdate() {
        document.removeEventListener("scroll", this.listener);
    }

    render() {
        return (
            <nav className="navbar navbar-expand-lg">

                <a
                    href="/"
                    className="navbar-brand font-weight-500"
                    onClick={evt => {
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Big Career Fest. Online',
                            label: 'NavBar'
                        });
                    }}>
                    Big Career Fest. Online
                </a>
                <button
                    className="navbar-toggler"
                    type="button"
                    data-toggle="collapse"
                    data-target="#navbarText"
                    aria-controls="navbarText"
                    aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon">

                    </span>
                </button>

                <div
                    className="collapse navbar-collapse"
                    id="navbarText">
                    <ul className="navbar-nav ml-auto mr-auto"/>
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="activities"
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click Activities',
                                        label: 'NavBar'
                                    });
                                }}
                                className={"nav-link"}
                            >
                                Навчання
                            </a>
                        </Link>
                    </li>

                        <p
                            style={{
                                backgroundColor: this.state.status === "top" ? "red" : "green",
                                color: this.state.status === "top" ? "white" : "blue",
                                position: "fixed"
                            }}
                        >
                            Amir
                        </p>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to={"companies"}
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF / NavBar',
                                        action: 'Click Companies',
                                    });
                                }}
                                className={"nav-link"}
                            >
                                Компанії
                            </a>
                        </Link>
                    </li>

                        <li className="nav-item">
                            <Link
                                activeClass="active"
                                className="test2"
                                to={"companies"}
                                spy={true}
                                smooth={true}
                                duration={1000}
                            >
                                <a
                                    onClick={evt => {
                                        ReactGA.event({
                                            category: 'Landing BCF / NavBar',
                                            action: 'Click Companies',
                                        });
                                    }}
                                    className={"nav-link"}
                                >
                                   Організатори
                                </a>
                            </Link>
                        </li>

                        <li className="nav-item">
                            <Link
                                activeClass="active"
                                className="test2"
                                to={"companies"}
                                spy={true}
                                smooth={true}
                                duration={1000}
                            >
                                <button className="registration-button-navbar">
                                    Реєстрація
                                </button>
                            </Link>
                        </li>

                    </ul>
                </div>
            </nav>
        );
    }
}
