import React from 'react';

import { withRouter } from "react-router-dom";

class NavBarActivities extends React.Component {
    constructor(props) {
        super(props);
    }

    handleClick = (action) => {
        if (this.props.location.pathname !== "/") {
            this.props.history.push(action);
            window.location.reload();
        } else {
            this.props.history.push(action);
        }
    };

    render() {
        return (
            <nav className="navbar navbar-expand-lg">
                <a className="navbar-brand" href="/">Big Career Fest. Online</a>
            </nav>
        );
    }
}

export default withRouter(NavBarActivities);
