import React from 'react';

import Countdown from "on-react-countdown";

import { Animated } from "react-animated-css";

import CardMiniTest from "./CardMiniTest";
import GiftCard from "../gifts/CardGift";
import CardTest from "../tests/CardTest";


export default class BlockMiniTests extends React.Component {
    render(){
        return(
            <div>
                <div className="container padding-minitest-top">
                    <div className="row">
                        <div className="col-12 col-lg-6">
                            <p className="stand-banner-title">Міні-тести</p>
                            <p className="stand-banner-explanation">
                                Поєднуй фан із користю! Проходь веселі міні-тести від компаній, щоб протестувати свої професійні навички та виграти призи. Мерч, кар’єрні консультації та можливість потрапити на співбесіду. Всього 10 хв між тобою і неймовірними можливостями
                            </p>
                        </div>

                        <div
                            className="col-12 col-lg-6 align-center justify-content-center-desktop">
                            <div>
                                {/*<div className="col-12 text-center">*/}
                                    {/*<p className="countdown-banner-text">*/}
                                        {/*Залишилось до ярмарки*/}
                                    {/*</p>*/}

                                    {/*<div className="countdown-timer mt-4">*/}
                                        {/*<Countdown*/}
                                            {/*alwaysDoubleDigit={true}*/}
                                            {/*wordsOff={true}*/}
                                            {/*text={{*/}
                                                {/*days: ['день', 'днів', 'днів'],*/}
                                                {/*hours: ['година', 'годин', 'годин'],*/}
                                                {/*minutes: ['хвилина', 'хвилини', 'хвилин'],*/}
                                                {/*seconds: ['секунда', 'секунд', 'секунд'],*/}
                                            {/*}}*/}
                                            {/*end={1601107200}*/}
                                        {/*/>*/}
                                    {/*</div>*/}
                                    {/*<div className="names-count-row justify-content-center">*/}
                                        {/*<p className="names-count">Днів</p>*/}
                                        {/*<p className="names-count hours-margin">Годин</p>*/}
                                        {/*<p className="names-count minutes-margin">Хвилин</p>*/}
                                        {/*<p className="names-count">Секунд</p>*/}
                                    {/*</div>*/}
                                {/*</div>*/}
                                <p className="countdown-banner-text">Ярмарок почався!</p>
                                <p className="banner-time">26-27 вересня. 11:00 - 18:00</p>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="container-fluid padding-tb-60">
                    <div className="row">
                        <div className="col-12">
                            <p className="gift-company-header font-weight-600">
                                Міні-тести
                            </p>
                        </div>
                    </div>

                    <div className="row margin-top-10">
                        <div className="col-12 text-center">
                            <span className="gift-company-subheader">
                                Проходь міні-тестування та отримуй крутецькі призи від компаній!
                            </span>
                            <br/>
                            <span className="gift-company-subheader margin-top-20">
                                Деталі читай у карточках нижче
                            </span>
                        </div>
                    </div>


                    <div className="row margin-top-20">
                        <CardMiniTest
                            image={"gift-aegis"}
                            name={"Dentsu Aegis Network"}
                            description={"Проходь тест «Куди податись в рекламі?» та отримуй брендовані подарунки."}
                            longDescription={"Хей! Проходь міні-тест «Куди податись в рекламі?», та отримуй у подарунок – бокс з книгою та брендованими подарунками від компанії. Звучить круто, чи не правда ж? Тримай лінк: https://www.tryinteract.com/share/quiz/5f69db72e48d1a00145420d6"}
                            standLink={"https://youtu.be/W7ipkmhsVPM"}
                        />
                        <CardMiniTest
                            image={"gift-genesis"}
                            name={"Genesis"}
                            description={`Проходь квіз та отримай брендовані подарунки. Посилання на квіз всередині...`}
                            longDescription={`Визначай свою digital-суперсилу за допомогою квізу та бери участь у розіграші брендованих подарунків. Лінк: https://bit.ly/3ciOxX2`}
                            standLink={"https://youtu.be/W7ipkmhsVPM"}
                        />
                        <CardMiniTest
                            image={"gift-jti"}
                            name={"JTI"}
                            description={"Проходь тестування - отримуй подарунки та призи. Цікаво? Читай далі!"}
                            longDescription={`Проходь тест за посиланням, який ти отримаєш під час трансляції JTI, та отримуй брендовані призи від компанії!`}
                            standLink={"https://youtu.be/W7ipkmhsVPM"}
                        />
                    </div>

                    <div className="row margin-top-20">
                        <CardMiniTest
                            image={"minitest-bakertilly"}
                            name={"Baker Tilly"}
                            description={"Не зволікай, проходь тест з бухгалтерського обліку!"}
                            longDescription={"Відвідуй \"Годину з компанією Baker Tilly\" у дні фесту. Під час неї, проходь тест з бухгалтерського обліку. Призи: персональна 15-хвилинна консультація з успішного проходження співбесіди від рекрутера компанії."}
                            standLink={"https://youtu.be/W7ipkmhsVPM"}
                        />
                        <CardMiniTest
                            image={"test-intertop"}
                            name={"Intertop"}
                            description={"Команда Інтертоп підготувала для тебе ТРИ крутих тестування з різних галузей."}
                            longDescription={`Команда Інтертоп підготувала для тебе ТРИ крутих тестування з різних галузей. Проходь їх, перших три переможця отримають лімітовані футболки INTERTOPxНОК.`}
                            links={[
                                "Тестування з інтерет-маркетингу: https://docs.google.com/forms/d/e/1FAIpQLScqaerZqKe2W0oJ3VTxUdQ1mE5y6Yk5PzQHeDxtLFl-7kmICw/viewform",
                                "Тестування з ІТ: https://docs.google.com/forms/d/e/1FAIpQLSfPfbdGHasc1EEszx6LoDdYZDgFjuE1uVPxtoxiclpKQ-V-wA/viewform.",
                                "Тестування з e-commerce: https://docs.google.com/forms/d/e/1FAIpQLSfjrKb_48lZwk4RFCwnAvx1ZDgIJH32ayu4uU5UdtdkBNXX6Q/viewform"
                            ]}
                            standLink={"https://youtu.be/W7ipkmhsVPM"}
                        />
                    </div>
                </div>

                <div className="container-fluid background-footer">
                    <div className="row">
                        <div className="container">
                            <div className="row">
                                <div className="col-12 col-lg-6 no-gutters-desktop">
                                    <p className="footer-words">© Highers, JCC NaUKMA, 2020</p>
                                </div>
                                <div className="col-lg-6 justify-content-end row-desktop no-gutters">
                                    <p className="footer-words">Big Career Fest. Online</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
