import React from "react";

import {
    useMediaQuery
} from "react-responsive";

import {
    Element,
    Link
} from "react-scroll/modules";

import * as ReactGA from "react-ga";

import CardPartners, {
    CardPartnersMobile
} from "./CardPartners";


const BlockInfoPartners = () => {
    const isTabletOrMobileDevice
        = useMediaQuery(
            {
                maxDeviceWidth: 1224
            }
        );

    if (isTabletOrMobileDevice) {
        return (
            <Element id="infopartners" name="infopartners" className="element">
                <div className={"container background-white margin-top-60"}>
                    <div className="row">
                        <div className="col-12">
                            <p
                                className={"landing-company-title-mobile font-weight-600"}>
                                Інфопартнери
                            </p>
                        </div>
                    </div>

                    <div className="row row-center">
                        <div className="col-12 col-lg-10">
                            <p className="title-block-expl font-weight-400">
                                Завдяки їм студенти тут
                            </p>
                        </div>
                    </div>

                    <div className="row row-eq-height margin-top-15">
                        <CardPartnersMobile
                            image={"partner-enactus"}
                        />
                        <CardPartnersMobile
                            image={"partner-business-events"}
                        />
                    </div>

                    <div className="row margin-top-15">
                        <CardPartnersMobile
                            image={"partner-business-opportunities"}
                        />
                        <CardPartnersMobile
                            image={"partner-naukma"}
                        />
                    </div>

                    <div className="row margin-top-15">
                        <CardPartnersMobile
                            image={"partner-kpi"}
                        />
                        <CardPartnersMobile
                            image={"partner-kneu"}
                        />
                    </div>

                    <div className="row margin-top-15">
                        <CardPartnersMobile
                            image={"partner-nau"}
                        />
                        <CardPartnersMobile
                            image={"partner-work-students"}
                        />
                    </div>

                    <div className="row margin-top-15">
                        <CardPartnersMobile
                            image={"partner-knu"}
                        />
                        <CardPartnersMobile
                            image={"partner-kau"}
                        />
                    </div>

                    <div className="row margin-top-15">
                        <CardPartnersMobile
                            image={"partner-knteu"}
                        />
                    </div>

                    <div className="row margin-top-50">
                        <div className="col-12 row-center">
                            <Link
                                spy={true}
                                smooth={true}
                                offset={50}
                                duration={500}
                                to="registration"
                                activeClass="active">
                                <button
                                    onClick={evt => {
                                        ReactGA.event({
                                            category: 'Landing BCF',
                                            action: 'Click Registration',
                                            label: 'Block Companies',
                                        });
                                    }}
                                    className="landing-company-button-registration font-weight-500">
                                    Працевлаштуватись
                                </button>
                            </Link>
                        </div>
                    </div>
                </div>
            </Element>
        );
    }

    return (
        <Element id="infopartners" name="infopartners" className="element">
            <div className="container background-white padding-tb-60">
                <div className="row">
                    <div className="col-12">
                        <p
                            className={"landing-company-title font-weight-600"}>
                            Інфопартнери
                        </p>
                    </div>
                </div>

                <div className="row row-center">
                    <div className="col-12 col-lg-10">
                        <p className="title-block-expl font-weight-400">
                            Завдяки їм студенти тут
                        </p>
                    </div>
                </div>

                <div className="row row-eq-height margin-top-20">
                    <CardPartners
                        image={"partner-enactus"}
                    />
                    <CardPartners
                        image={"partner-naukma"}
                    />
                    <CardPartners
                        image={"partner-business-events"}
                    />
                    <CardPartners
                        image={"partner-kpi"}
                    />
                    <CardPartners
                        image={"partner-business-opportunities"}
                    />
                    <CardPartners
                        image={"partner-kneu"}
                    />
                </div>

                <div className="row margin-top-20">
                    <CardPartners
                        image={"partner-nau"}
                    />
                    <CardPartners
                        image={"partner-work-students"}
                    />
                    <CardPartners
                        image={"partner-knteu"}
                    />
                    <CardPartners
                        image={"partner-kau"}
                    />
                    <CardPartners
                        image={"partner-knu"}
                    />
                </div>

                <div className="row margin-top-50">
                    <div className="col-12 row-center">
                        <Link
                            spy={true}
                            smooth={true}
                            offset={50}
                            duration={500}
                            to="registration"
                            activeClass="active">
                            <button
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click Registration',
                                        label: 'Block Companies',
                                    });
                                }}
                                className="landing-company-button-registration font-weight-500">
                                Працевлаштуватись
                            </button>
                        </Link>
                    </div>
                </div>
            </div>
        </Element>
    );
};


export {
    BlockInfoPartners as default
};
