import React from 'react';

import { Link as RouterLink } from "react-router-dom";

import {
    withRouter
} from "react-router-dom";

import {
    useMediaQuery
} from "react-responsive";

import {
    Link,
} from 'react-scroll';

import * as ReactGA from "react-ga";

const NavBar = () => {

    const isTabletOrMobileDevice
        = useMediaQuery(
        {
            maxDeviceWidth: 1224
        }
    );


        return (
            <nav className="navbar navbar-expand-lg">
                <a
                    href="/"
                    className="navbar-brand font-weight-500"
                    onClick={evt => {
                        ReactGA.event({
                            category: 'Landing BCF',
                            action: 'Click Big Career Fest. Online',
                            label: 'NavBar'
                        });
                    }}>
                    Big Career Fest. Online
                </a>
                <button
                    className="navbar-toggler"
                    type="button"
                    data-toggle="collapse"
                    data-target="#navbarText"
                    aria-controls="navbarText"
                    aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon">

                    </span>
                </button>

                <div
                    className="collapse navbar-collapse"
                    id="navbarText">
                    <ul className="navbar-nav ml-auto mr-auto"/>
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to="activities"
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF',
                                        action: 'Click Activities',
                                        label: 'NavBar'
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Навчання
                            </a>
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            className="test2"
                            to={"companies"}
                            spy={true}
                            smooth={true}
                            duration={1000}
                        >
                            <a
                                onClick={evt => {
                                    ReactGA.event({
                                        category: 'Landing BCF / NavBar',
                                        action: 'Click Companies',
                                    });
                                }}
                                className={
                                    isTabletOrMobileDevice
                                        ? "nav-link font-weight-400"
                                        : "nav-link font-weight-500"
                                }>
                                Компанії
                            </a>
                        </Link>
                    </li>

                        <li className="nav-item">
                            <Link
                                activeClass="active"
                                className="test2"
                                to={"companies"}
                                spy={true}
                                smooth={true}
                                duration={1000}
                            >
                                <a
                                    onClick={evt => {
                                        ReactGA.event({
                                            category: 'Landing BCF / NavBar',
                                            action: 'Click Companies',
                                        });
                                    }}
                                    className={
                                        isTabletOrMobileDevice
                                            ? "nav-link font-weight-400"
                                            : "nav-link font-weight-500"
                                    }>
                                   Організатори
                                </a>
                            </Link>
                        </li>

                        <li className="nav-item">
                            <Link
                                activeClass="active"
                                className="test2"
                                to={"companies"}
                                spy={true}
                                smooth={true}
                                duration={1000}
                            >
                                <button className="registration-button-navbar">
                                    Реєстрація
                                </button>
                            </Link>
                        </li>

                    </ul>
                </div>
            </nav>
        );
};

export default withRouter(NavBar);
